				+ parseInt(ds_list7.GetColumn(0,"BEZEI4ZTON6")) + parseInt(ds_list7.GetColumn(0,"BEZEI5ZTON6")) + parseInt(ds_list7.GetColumn(0,"BEZEI6ZTON6")) 
