			vOrgZTOn13 = ds_list_org.GetColumn(0, "ZTON13");
		}

		if(length(trim(ds_list_org.GetColumn(0, "ZTON14") )) == 0)
		{
			vOrgZTOn14 = "0";		 
		}
		else
		{
			vOrgZTOn14 = ds_list_org.GetColumn(0, "ZTON14");
		}

		if(length(trim(ds_list_org.GetColumn(0, "ZTON15") )) == 0)
		{
			vOrgZTOn15 = "0";		 
		}
		else
		{
			vOrgZTOn15 = ds_list_org.GetColumn(0, "ZTON15");
		}



		if( (vOrgZTOn1 != ed_zton1.value ) || (vOrgZTOn2 != ed_zton2.value ) || (vOrgZTOn3 != ed_zton3.value ) || (vOrgZTOn4 != ed_zton4.value ) ||
			(vOrgZTOn5 != ed_zton5.value ) || (vOrgZTOn6 != ed_zton6.value ) || (vOrgZTOn7 != ed_zton7.value ) || (vOrgZTOn8 != ed_zton8.value ) ||
			(vOrgZTOn9 != ed_zton9.value ) || (vOrgZTOn10 != ed_zton10.value ) || (vOrgZTOn11 != ed_zton11.value ) || (vOrgZTOn12 != ed_zton12.value ) ||
			(vOrgZTOn13 != ed_zton13.value ) || (vOrgZTOn14 != ed_zton14.value ) || (vOrgZTOn15 != ed_zton15.value ) )
		{
			ds_list.SetColumn(0,"CHNGCONFIRM", "Y");
		}
		else{ 
			ds_list.SetColumn(0,"CHNGCONFIRM", ds_list_org.GetColumn(0, "CHNGCONFIRM"));
		}
	}



	ds_setLifnr.setColumn(0, "ERDAT", cal_erdat.Value);
	if(me_erzet.Value > 1200)
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "P");
	}
	else
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "A");
	}



	ds_setLifnr.setColumn(0, "LIFNR", INIT_LIFNR);

	ds_setLifnr.setColumn(0, "BERDAT", Replace(FIRST_ERDAT, ".", ""));

	var totTonSum = parseInt(ed_zton1.value) + parseInt(ed_zton2.value) + parseInt(ed_zton3.value) + parseInt(ed_zton4.value) + parseInt(ed_zton5.value) + 
				  parseInt(ed_zton6.value) + parseInt(ed_zton7.value) + parseInt(ed_zton8.value) + parseInt(ed_zton9.value) + parseInt(ed_zton10.value) +
				  parseInt(ed_zton11.value) + parseInt(ed_zton12.value) + parseInt(ed_zton13.value) + parseInt(ed_zton14.value) + parseInt(ed_zton15.value);  



	ds_setLifnr.setColumn(0, "ZTON", parseInt(totTonSum));



	// 저장 처리 
	tit_clearActionInfo();



	// 저장 처리 
	tit_addMultiActionInfo("mm05:MM0501003B_U12");
	tit_addMultiActionInfo("mm05:MM0501003B_U13");





	tit_callService(
        ""
        , ""
        , "ds_setLifnr=ds_setLifnr"
        , ""
        , ""
        , "fn_checkLifnrChg");

}


function fn_checkLifnrChg(errCode, errMsg) 
{
	//trace(ds_list.SaveXML());
	tit_clearActionInfo();

    trace(ds_list.SaveXML());
	tit_callService(
        "MM0501003B_ACT1"
        , "save2"
        , "ds_list=ds_list"
        , ""
        , ""
        , "fn_afterSaveChg"
        , false);
}






/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterSaveChg(errCode, errMsg) 
{

	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		var arr = 1;
		close(arr);
	}
}
