function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm();
	//gfn_initDs(ds_cond, true);

	// 출하예정 상세 정보 화면의 그리드에서 넘어온 순번
	if ( isExistVar("seq_n", true) ) {	   
	   //ds_list6.SetColumn(0,"BEZEI_NM", vstel);
	   SEQ_N = seq_n;
	}



	// 출하예정 상세의 화면 상단에서 설정된 초기 톤정보	
	if(SEQ_N == 0)
	{
		M_ZTON1 = dslist_copy.getcolumn(0,"ZTON1");
		M_ZTON2 = dslist_copy.getcolumn(0,"ZTON2");			
		M_ZTON3 = dslist_copy.getcolumn(0,"ZTON3");			
		M_ZTON4 = dslist_copy.getcolumn(0,"ZTON4");			
		M_ZTON5 = dslist_copy.getcolumn(0,"ZTON5");			
		M_ZTON6 = dslist_copy.getcolumn(0,"ZTON6");			
		M_ZTON7 = dslist_copy.getcolumn(0,"ZTON7");			
		M_ZTON8 = dslist_copy.getcolumn(0,"ZTON8");			
		M_ZTON9 = dslist_copy.getcolumn(0,"ZTON9");			
		M_ZTON10 = dslist_copy.getcolumn(0,"ZTON10");	
		M_ZTON11 = dslist_copy.getcolumn(0,"ZTON11");	
		M_ZTON12 = dslist_copy.getcolumn(0,"ZTON12");	
		M_ZTON13 = dslist_copy.getcolumn(0,"ZTON13");	
		M_ZTON14 = dslist_copy.getcolumn(0,"ZTON14");	
		M_ZTON15 = dslist_copy.getcolumn(0,"ZTON15");	
	}else	{
		M_ZTON1 = ds_list.getcolumn(0,"ZTON1");
		M_ZTON2 = ds_list.getcolumn(0,"ZTON2");			
		M_ZTON3 = ds_list.getcolumn(0,"ZTON3");			
