var F_TIMEOUT = 0;
var saveFlag = "N";
var vstel = "";
var lifnrValue = "";
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// HTTP TIMEOUT 설정
	F_TIMEOUT = HTTP.TimeOut;

	//출하기간 : 현재일자-한달 ~ 현재일자=7일
	cal_datef.value = gfn_getBeforeDate(gfn_getCurrDate(),30);
	cal_datet.value = gfn_getAfterDate(gfn_getCurrDate(),7);
	cb_vstel.Index = 0;
	gfn_showMsg("CI00007");
}

