/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	var arr = "";
	var pspid = "";
	var posid = "";
	if(gfn_isNull(ed_pjt, true, st_pjt.value))
		return;
	if(gfn_isNull(cb_vstel, true, st_vstel.value))
		return;	
	// 필수 입력사항 및 일자 체크 
	if ( !gfn_checkFromToObj(cal_datef, cal_datet, true, st_date.value) )
		return;

	if(length(trim(ed_pjt.value)) < 6)
	{
		arr = [st_pjt.value, 6];
		gfn_showMsg("CW00174",arr);
		ed_pjt.SetFocus();
		return;
	}
	else
	{
		if(substr(ds_cond.GetColumn(0,"PSPID"),0,1) == "Q")
		{
			pspid = ds_cond.GetColumn(0,"PSPID");
			posid = "";
		}
		else if(substr(ds_cond.GetColumn(0,"PSPID"),0,2) == 'QM')
		{
			pspid = ds_cond.GetColumn(0,"PSPID");
			posid = "";
		}
		else if(substr(ds_cond.GetColumn(0,"PSPID"),6,1) == "N") //보수현장 예외처리 - 정병기요청 20090421
		{
			pspid = ds_cond.GetColumn(0,"PSPID");
			posid = "";
		}
		else
		{
			if(substr(ds_cond.GetColumn(0,"PSPID"),0,1) == 'V')
			{
				pspid = substr(ds_cond.GetColumn(0,"PSPID"),0,7);
			}
			else
			{
				pspid = substr(ds_cond.GetColumn(0,"PSPID"),0,6);
			}

			posid = ds_cond.GetColumn(0,"PSPID");
		}	
	}

	if(length(trim(ed_matnr.value)) > 0 && length(trim(ed_matnr.value)) < 3)
	{
		arr = [st_matnr.value, 3];
		gfn_showMsg("CW00174",arr);
		ed_matnr.SetFocus();
		return;
	}

	ds_cond_back.SetColumn(0,"PSPID",pspid);
	if(cb_vstel.Value == "1200")
	{
		ds_cond_back.SetColumn(0,"VSTEL", "1100");
	}
	else if(cb_vstel.Value == "1300")
	{
		ds_cond_back.SetColumn(0,"VSTEL", "1100");
	}
	else
	{
		ds_cond_back.SetColumn(0,"VSTEL",ds_cond.GetColumn(0,"VSTEL"));
	}
	ds_cond_back.SetColumn(0,"ZZPROF",ds_cond.GetColumn(0,"ZZPROF"));
	ds_cond_back.SetColumn(0,"ZZPROT",ds_cond.GetColumn(0,"ZZPROT"));
	ds_cond_back.SetColumn(0,"MATNR",ds_cond.GetColumn(0,"MATNR"));
	ds_cond_back.SetColumn(0,"POSID",posid);

	//출하예정상세화면에서 출발지 지정 - 20091007 이은기cj
	vstel = cb_vstel.Value;

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list2);
	ds_list2.row = -1; 

	grd_list.SetCellProp("head", 0, "Text", 0);
	grd_list2.SetCellProp("head", 0, "Text", 0);

	http.TimeOut = 1800;

	// 조회 처리 
	tit_clearActionInfo();
	tit_callService(
        "MM0502001A_ACT"
        , "query"
        , "ds_cond=ds_cond_back"
        , "ds_list=ds_list1 ds_list2=ds_list2"
        , ""
        , "fn_afterQuery"
        , false);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}
/*
	if(length(trim(ds_list.getcolumn(0,"VTEXT"))) == 0 )
	{
		gfn_showMsg("CI00021","운송지역");
		return;
	}
*/

	if(trim(ds_list.getcolumn(ds_list.row,"ZSTAT4")) == "D") {
		alert("건산법 문제로 출하가 차단 된 프로젝트입니다.");
		return;
	}

	ds_list5.ClearData();
	dslist5_copy.ClearData();
	var arg = "pjt="+quote(ed_pjt.Text)+" && vstel = "+quote(cb_vstel.Value)+"&& lifnrValue = "+quote(cb_lifnr.Value);
	gfn_openPopup("MM0502001B", true, arg);

}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save2() {
	//system적으로 2번 저장 트랜젝션을 호출하는 경우를 방지하기 위해
	if(saveFlag == 'N') return;
	else saveFlag = "N";

	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}

	ds_list2.Filter("1==1");
	ds_list3.DeleteAll();
	ds_list3.Copy(ds_list2);
	ds_list2.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(ds_list.row,"EXTWG"))) +" && AUFNR == "+quote(TRIM(ds_list.GetColumn(ds_list.row,"AUFNR"))));
	ds_list4.ClearData();

	var nRow = 0;
	for( var i=0; i<ds_list.rowcount; i++ )
	{
		if (ds_list.GetColumn(i,"FLAG") == "U")
		{
			ds_list3.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(i,"EXTWG"))) +" && AUFNR == "+quote(TRIM(ds_list.GetColumn(i,"AUFNR"))) );

			for( var j=0; j<ds_list3.rowcount; j++ )
			{
				if(trim(ds_list3.GetColumn(j,"ZSTAT4")) == '1' || trim(ds_list3.GetColumn(j,"ZSTAT4")) == '2' || trim(ds_list3.GetColumn(j,"ZSTAT4")) == '6' )
				{
					if((ds_list3.GetColumn(j,"EXTWG") == "BOX33" || ds_list3.GetColumn(j,"EXTWG") == "BOX46" || ds_list3.GetColumn(j,"EXTWG") == "BOX28" || ds_list3.GetColumn(j,"EXTWG") == "BOX01" || ds_list3.GetColumn(j,"EXTWG") == "BOX02" || ds_list3.GetColumn(j,"EXTWG") == "BOX31" || ds_list3.GetColumn(j,"EXTWG") == "BOX13" || ds_list3.GetColumn(j,"EXTWG") == "BOX14" || ds_list3.GetColumn(j,"EXTWG") == "BOX17" || ds_list3.GetColumn(j,"EXTWG") == "BOX19" || ds_list3.GetColumn(j,"EXTWG") == "BOX20" || ds_list3.GetColumn(j,"EXTWG") == "BOX32" || ds_list3.GetColumn(j,"EXTWG") == "BOX34" || ds_list3.GetColumn(j,"EXTWG") == "BOX40" || ds_list3.GetColumn(j,"EXTWG") == "BOX43") && ds_list3.GetColumn(j,"FLAG") != "U")
					{
						continue;
					}

					ds_list4.AddRow();

					ds_list4.SetColumn(nRow,"MANDT",ds_list3.GetColumn(j,"MANDT"));
					ds_list4.SetColumn(nRow,"POSID2",ds_list3.GetColumn(j,"POSID2"));
					ds_list4.SetColumn(nRow,"POST1",ds_list3.GetColumn(j,"POST1"));
					ds_list4.SetColumn(nRow,"BDTER",replace(ds_list3.GetColumn(j,"BDTER"),"-",""));
					ds_list4.SetColumn(nRow,"EL_ATYP",ds_list3.GetColumn(j,"EL_ATYP"));
					ds_list4.SetColumn(nRow,"EL_ZAFQ",ds_list3.GetColumn(j,"EL_ZAFQ"));
					ds_list4.SetColumn(nRow,"AUFNR",ds_list3.GetColumn(j,"AUFNR"));
					ds_list4.SetColumn(nRow,"KTEXT",ds_list3.GetColumn(j,"KTEXT"));
					ds_list4.SetColumn(nRow,"EXTWG",ds_list3.GetColumn(j,"EXTWG"));
					ds_list4.SetColumn(nRow,"RSNUM",ds_list3.GetColumn(j,"RSNUM"));
					ds_list4.SetColumn(nRow,"RSPOS",ds_list3.GetColumn(j,"RSPOS"));
					ds_list4.SetColumn(nRow,"MATNR",ds_list3.GetColumn(j,"MATNR"));
					ds_list4.SetColumn(nRow,"MAKTX",ds_list3.GetColumn(j,"MAKTX"));
					ds_list4.SetColumn(nRow,"MATKL",ds_list3.GetColumn(j,"MATKL"));
					ds_list4.SetColumn(nRow,"ZMTART",ds_list3.GetColumn(j,"ZMTART"));
					if (LENGTH(TRIM(ds_list3.GetColumn(j,"ERFMG"))) == 0)
						ds_list4.setcolumn(nRow,"ERFMG","0");
					else
						ds_list4.SetColumn(nRow,"ERFMG",ds_list3.GetColumn(j,"ERFMG"));
					if (LENGTH(TRIM(ds_list3.GetColumn(j,"ENMNG"))) == 0)
						ds_list4.setcolumn(nRow,"ENMNG","0");
					else
						ds_list4.SetColumn(nRow,"ENMNG",ds_list3.GetColumn(j,"ENMNG"));
					if (LENGTH(TRIM(ds_list3.GetColumn(j,"PRLAB"))) == 0)
						ds_list4.setcolumn(nRow,"PRLAB","0");
					else
						ds_list4.SetColumn(nRow,"PRLAB",ds_list3.GetColumn(j,"PRLAB"));
					if (LENGTH(TRIM(ds_list3.GetColumn(j,"LFIMG"))) == 0)
						ds_list4.setcolumn(nRow,"LFIMG","0");
					else
						ds_list4.SetColumn(nRow,"LFIMG",ds_list3.GetColumn(j,"LFIMG"));
					ds_list4.SetColumn(nRow,"ERFME",ds_list3.GetColumn(j,"ERFME"));
					ds_list4.SetColumn(nRow,"LGORT",ds_list3.GetColumn(j,"LGORT"));
					ds_list4.SetColumn(nRow,"LGOBE",ds_list3.GetColumn(j,"LGOBE"));
					ds_list4.SetColumn(nRow,"KUNNR",ds_list3.GetColumn(j,"KUNNR"));
					ds_list4.SetColumn(nRow,"NAME1",ds_list3.GetColumn(j,"NAME1"));
					ds_list4.SetColumn(nRow,"WADAT",replace(ds_list3.GetColumn(j,"WADAT"),"-",""));
					ds_list4.SetColumn(nRow,"VSTEL",ds_list3.GetColumn(j,"VSTEL"));
					ds_list4.SetColumn(nRow,"KUNWE",ds_list3.GetColumn(j,"KUNWE"));
					ds_list4.SetColumn(nRow,"VKORG",ds_list3.GetColumn(j,"VKORG"));
					ds_list4.SetColumn(nRow,"VTWEG",ds_list3.GetColumn(j,"VTWEG"));
					ds_list4.SetColumn(nRow,"SPART",ds_list3.GetColumn(j,"SPART"));
					ds_list4.SetColumn(nRow,"VBELN",ds_list3.GetColumn(j,"VBELN"));
					ds_list4.SetColumn(nRow,"POSNR",ds_list3.GetColumn(j,"POSNR"));
					ds_list4.SetColumn(nRow,"ZSTAT1","X");
					ds_list4.SetColumn(nRow,"ZSTAT2",ds_list3.GetColumn(j,"ZSTAT2"));
					ds_list4.SetColumn(nRow,"ZSTAT3",ds_list3.GetColumn(j,"ZSTAT3"));
					ds_list4.SetColumn(nRow,"ZSTAT4",ds_list3.GetColumn(j,"ZSTAT4"));
					ds_list4.SetColumn(nRow,"ZLOG1",ds_list3.GetColumn(j,"ZLOG1"));
					ds_list4.SetColumn(nRow,"ZLOG2",ds_list3.GetColumn(j,"ZLOG2"));
					ds_list4.SetColumn(nRow,"ZLOG3",ds_list3.GetColumn(j,"ZLOG3"));
					ds_list4.SetColumn(nRow,"LZONE",ds_lzone.GetColumn(0,"LZONE"));
					ds_list4.SetColumn(nRow,"ZCANC",ds_list3.GetColumn(j,"ZCANC"));
					ds_list4.SetColumn(nRow,"ZKNUM",ds_list3.GetColumn(j,"ZKNUM"));
					ds_list4.SetColumn(nRow,"ZKNUM2",ds_list3.GetColumn(j,"ZKNUM2"));
					ds_list4.SetColumn(nRow,"PSPID",ds_list3.GetColumn(j,"PSPID"));
					ds_list4.SetColumn(nRow,"MATNR_NM",ds_list3.GetColumn(j,"MATNR_NM"));
					ds_list4.SetColumn(nRow,"SPEC",ds_list3.GetColumn(j,"SPEC"));
					ds_list4.SetColumn(nRow,"ZSIZE",ds_list3.GetColumn(j,"ZSIZE"));
					//자장위치2
					ds_list4.SetColumn(nRow,"LGORT2",ds_list3.GetColumn(j,"LGORT2"));
					ds_list4.SetColumn(nRow,"LGOBE2",ds_list3.GetColumn(j,"LGOBE2"));
					ds_list4.SetColumn(nRow,"FLAG","U");

					nRow++;
				}
			}
		} //if	
		ds_list3.Filter("1==1");
	} //for

	if(ds_list4.rowcount == 0) return;

	if(length(trim(ds_list5.getcolumn(0,"TPLST"))) == 0 )
	{
		gfn_showMsg("CI00021","출발지");
		return;
	}
	// HTTP SESSION TIMEOUT을 변경
	http.TimeOut = 1800;

	// trace(ds_list5.SaveXML());
	// gfn_showalert("SAVE.XML");
	// return;
	trace(ds_list5.SaveXML());

	tit_clearActionInfo();
	tit_callService(
        "MM0502001B_ACT"
        , "save"
        , "ds_list=ds_list4 ds_list2=ds_list5"
        , ""
        , ""
        , "fn_afterSave");

}
/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {
	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}

	ds_list4.ClearData();
	var nRow = 0;
	for( var i=0; i<ds_list.rowcount; i++ )
	{
		if (ds_list.GetColumn(i,"FLAG") == "U")
		{
			ds_list3.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(i,"EXTWG"))) +" && AUFNR == "+quote(TRIM(ds_list.GetColumn(i,"AUFNR"))) );
			for( var j=0; j<ds_list3.rowcount; j++ )
			{
				if(trim(ds_list3.GetColumn(j,"ZSTAT4")) == '4')
				{
					ds_list4.AddRow();
					ds_list4.SetColumn(nRow,"ZBELN",ds_list3.GetColumn(j,"ZBELN"));
					ds_list4.SetColumn(nRow,"ZOSNR",ds_list3.GetColumn(j,"ZOSNR"));
					ds_list4.SetColumn(nRow,"FLAG","U");
				}
				nRow++;
			}
		} //if	
		ds_list3.Filter("1==1");
	} //for

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        ""
        , "mm05:MM0502001A_D1"
        , ""
        , "FLAG"
        , ""
        , -1
        , "");

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list4:A"
        , ""
        , ""
        , "fn_afterDelete");
}
