/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(grd_list, errCode, errMsg);

	ds_cond_back.SetColumn(0,"PSPID","");
	ds_cond_back.SetColumn(0,"ZZPROF","");
	ds_cond_back.SetColumn(0,"ZZPROT","");
	ds_cond_back.SetColumn(0,"VSTEL","");
	ds_cond_back.SetColumn(0,"MATNR","");
	ds_cond_back.SetColumn(0,"POSID","");

	ds_list2.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(0,"EXTWG"))) +" && AUFNR == "+quote(TRIM(ds_list.GetColumn(0,"AUFNR"))));
/*
	if(ds_list.rowcount > 0 && length(trim(ds_list.getcolumn(0,"VTEXT"))) == 0 )
	{
		gfn_showAlert("CI00021","운송지역");
	}
*/	
	saveFlag = "Y";
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	// HTTP TIMEOUT 설정
    http.TimeOut = F_TIMEOUT;

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00004");
		fn_query();
	}
}

