/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list2_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{ 
	if ( nCell == 0 && ((ds_list.GetColumn(ds_list.row,"BOX_GUBN") != "신박스" && (ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX33' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX46' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX28' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX01' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX02' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX31' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX13' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX14' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX17' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX19' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX20' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX32' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX34' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX40' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX43'))
	                       || (ds_list.GetColumn(ds_list.row,"BOX_GUBN") == "신박스" && (ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX03' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX04' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX06' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX16' || 
	                                                                              ds_list.GetColumn(ds_list.row,"EXTWG") == 'BOX24')))) {
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		gfn_sortGrid(obj,nCell);
	}
}
