/********************************************************************************
* 기      능   : 상태가 '3','5','6','7','8'인 경우 check box disable해준다.
********************************************************************************/
function fn_disable(ZSTAT4)
{
	if(ZSTAT4 == '3' || ZSTAT4 == '4' || ZSTAT4 == '5' || ZSTAT4 == '7' || ZSTAT4 == '8' || ZSTAT4 == '9' || ZSTAT4='C') return "none";
	else return "checkbox";
}
/********************************************************************************
* 기      능   : BOX가 'BOX33','BOX46','BOX28'인 경우 check box enable해준다.
********************************************************************************/
function fn_disable2(EXTWG,POSID2)
{
     // 신박스 기준으로 변경
	// if(trim(EXTWG) == 'BOX33' || trim(EXTWG) == 'BOX46' || trim(EXTWG) == 'BOX28' || 
	   // trim(EXTWG) == 'BOX01' || trim(EXTWG) == 'BOX02' || trim(EXTWG) == 'BOX31' || 
	   // trim(EXTWG) == 'BOX13' || trim(EXTWG) == 'BOX14' || trim(EXTWG) == 'BOX17' || 
	   // trim(EXTWG) == 'BOX19' || trim(EXTWG) == 'BOX20' || trim(EXTWG) == 'BOX32' || 
	   // trim(EXTWG) == 'BOX34' || trim(EXTWG) == 'BOX40' || trim(EXTWG) == 'BOX43') return "checkbox";
	if(trim(EXTWG) == 'BOX03' || trim(EXTWG) == 'BOX04' || trim(EXTWG) == 'BOX06' || 
	   trim(EXTWG) == 'BOX16' || trim(EXTWG) == 'BOX24' || substr(POSID2,6,1) != "L") return "checkbox";  // 물류, PSD 현장의 경우 선택 가능 13.08.30 HSS
	else return "none";
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && (ds_list.GetColumn(nRow,"ZSTAT4") == "1" || ds_list.GetColumn(nRow,"ZSTAT4") == "2" || ds_list.GetColumn(nRow,"ZSTAT4") == "6" || ds_list.GetColumn(nRow,"ZSTAT4") == "D" ))
			{
				ds_list.SetColumn(nRow,"FLAG","U");
				ds_list.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"CHK","0");
			}
		break;
	}	
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && (ds_list2.GetColumn(nRow,"ZSTAT4") == "1" || ds_list2.GetColumn(nRow,"ZSTAT4") == "2" ||ds_list2.GetColumn(nRow,"ZSTAT4") == "6" ))
			{
				ds_list2.SetColumn(nRow,"FLAG","U");
				ds_list2.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list2.SetColumn(nRow,"FLAG","");
				ds_list2.SetColumn(nRow,"CHK","0");
			}
		break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 리스트의 폰트 색깔 처리
********************************************************************************/
function ufn_setColor(obj,stat4)
{
	cValue = decode(stat4,'1','default','2','default','6','default','3','red','4','blue','5','blue','7','blue','8','blue','9','blue','C','red','D','red');
	//선택된 로우의 Font Color
	obj.SelColor = cValue;
	//Grid의 Font Color
	return cValue;
}
/********************************************************************************
* 기      능   : 자재검색 클릭
********************************************************************************/
function btn_matnr_OnClick(obj,nRow,nCell,strVal,nPivotIndex)
{
	var matnr = gfn_openPopup("MM0901002A",true); 

	if (matnr !=null && obj == btn_matnr) 
	{
		ed_matnr.value = matnr;
	}
}

