// [ PART 1 ]
// Form에서 사용될 전역변수를 선언
// FORM에서 사용되는 전역변수는 대문자로 작성하며, F_XXX 의 형태를 갖도록 한다.
//=========================================================================================
var E_FLAG;
var cVstel;	
var INIT_LIFNR; // 배송업체 정보
var RECEIV_LIFNR; // 출하예정현황 화면에서 전달받은 운송업체 

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm();

	ds_list5.ClearData();
	ds_list5.addrow();

	gfn_initDs(ds_date_, true);
	ck_input.value = 0;

	ck_fullset.Value = 0;
	ck_fullset_1.Value = 0;

	cal_erdat.value = gfn_getAfterDate(gfn_getCurrDate(),1);
	cal_dpten.value = gfn_getAfterDate(gfn_getCurrDate(),1);

	//조회조건이 이천일 경우 이천자재만 조회되고,천안일 경우 이천,천안자재가 조회되서 
	//출발지 정보를 조회조건의 출발지정보로 수정- 20091007 이은기cj
	//ds_plst.Filter("CODE=="+quote(trim(ds_list2.getcolumn(0,"VSTEL"))));
	ds_plst.Filter("CODE=="+quote(trim(vstel)));
	cb_plst.index = 0;

	var fa_pspid = ds_list.getcolumn(0,"POSID2");
	if (substr(fa_pspid,0,2) == "QM")
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,2,6));
	}
	else if (substr(fa_pspid,0,1) == "Q")
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,1,6));
	}
	else
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,0,6));
	}

	//도착지명 display
	if(length(trim(ds_list.getcolumn(0,"VTEXT"))) == 0 )
	{
		ck_trcost.Value = '1';
	}

	ds_date_.SetColumn(0,"HOCHA",gfn_getAfterDate(gfn_getCurrDate(),1));

	/*
	if(vstel == "1100")	{
		btn_Getton.Text = "천안 톤 정보 설정";
	}		
	else{
		btn_Getton.Text = "이천 톤 정보 설정";
	}
	*/

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0502001B_S1", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S2", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S3", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S5", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S6", null, true);

	//tit_addSearchActionInfo("mm05:MM0502001B_S7", null, true);




	tit_callService(
        ""
        , ""
        , "ds_date_=ds_date_ ds_cond_lifnr=ds_cond_lifnr"
        , "ds_hocha=ds_hocha ds_lifnr=ds_lifnr ds_hyun=ds_hyun ds_addr=ds_addr ds_area=ds_area ds_list_lifnr=ds_list_lifnr"
        , ""
        , "fn_afterLifnr"
        , true);


		if ( isExistVar("lifnrValue", true) ) {	 

			RECEIV_LIFNR = lifnrValue;

		};
}
//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 호차정보조회
********************************************************************************/
function fn_getHocha(hocha)
{
	ds_date_.SetColumn(0,"HOCHA",hocha);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0502001B_S1", null, true);
	tit_callService(
        ""
        , ""
        , "ds_date_=ds_date_"
        , "ds_hocha=ds_hocha"
        , ""
        , "fn_afterHocha"
        , true);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

	//Fullset, Fullset(Jamb포함) 여부 파악 2012.07.23 이호영 
	if (ck_fullset.Value == "1" && ck_fullset_1.Value == "1") 
	{
		gfn_showalert("FULL SET, FULL SET(JAMB)중 하나의 값만 체크하십시요");
		return;
	}



	if(length(trim(cb_hocha.value)) == 0)
	{
		if(ck_input.Value == "1")
		{

		}
		else if(ck_input.Value == "0" &&
				(ed_zton1.value == "0" || ed_zton1.value == "") &&
				(ed_zton2.value == "0" || ed_zton2.value == "") &&
				(ed_zton3.value == "0" || ed_zton3.value == "") &&
				(ed_zton4.value == "0" || ed_zton4.value == "") &&
				(ed_zton5.value == "0" || ed_zton5.value == "") &&
				(ed_zton6.value == "0" || ed_zton6.value == "") &&
				(ed_zton7.value == "0" || ed_zton7.value == "") &&
				(ed_zton8.value == "0" || ed_zton8.value == "") &&
				(ed_zton9.value == "0" || ed_zton9.value == "") &&
				(ed_zton10.value == "0" || ed_zton10.value == "") &&
				(ed_zton11.value == "0" || ed_zton11.value == "") &&
				(ed_zton12.value == "0" || ed_zton12.value == "") &&
				(ed_zton13.value == "0" || ed_zton13.value == "") &&
				(ed_zton14.value == "0" || ed_zton14.value == "") &&
				(ed_zton15.value == "0" || ed_zton15.value == ""))
		{
			gfn_showMsg("CW00001",st_ton.Value);
			return;
		}


		// 필수 입력사항 및 일자 체크
		if ( !gfn_checkFromToObj(cal_erdat, cal_dpten, true, st_date.value) )
			return;
		if (cal_erdat.Value == cal_dpten.Value && me_erzet.Value > me_upten.Value)
		{
			gfn_showMsg("CW00022",st_upten.value);
			me_upten.SetFocus();
			return;
		}

		if(ck_input.Value == "0")
		{
			if(length(me_erzet.Value) != 4)
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(me_erzet.Value == "0000" || me_erzet.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(substr(me_erzet.Value,0,2) > "24" || substr(me_erzet.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			if(length(me_upten.Value) != 4)
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(me_upten.Value == "0000" || me_upten.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(substr(me_upten.Value,0,2) > "24" || substr(me_upten.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
		}	

	}


	if(length(trim(ds_list.getcolumn(0,"VTEXT"))) == 0 && ck_trcost.Value == '0')
	{
		gfn_showMsg("CI00021","운송지역");
		return;
	}







	/*
	저장전 validation 체크
	1. 그리드의 출발지가 선택되어 있는데 차량 총수량과 톤수정보가 입력이 되지 않은 경우 체크
	2. 톤정보 전체합과 출발지상세정보 차량총수량 합계가 일치하는지 확인
	*/


	for(var i=0; i < 7 ; i++) {				
		var iChk =0;

		if(length(trim(grd_list3.GetCellValue(i, 2) )) != 0) iChk++;
		if(length(trim(grd_list3.GetCellValue(i, 3) )) != 0) iChk++;
		if(length(trim(grd_list3.GetCellValue(i, 4) )) != 0) iChk++;


		if(iChk != 0 && iChk != 3)	{	
			gfn_showAlert( (i+1) + " 행의 출발지, 차량총수량, 톤수정보를 모두 입력해주세요." );			
			return;
		}
	}



