	}


	var tonSum1; // 톤 정보가 입력된 총 차량대수
	tonSum1 = parseInt(ed_zton1.value) + parseInt(ed_zton2.value) + parseInt(ed_zton3.value) + parseInt(ed_zton4.value) + parseInt(ed_zton5.value) + 
			  parseInt(ed_zton6.value) + parseInt(ed_zton7.value) + parseInt(ed_zton8.value) + parseInt(ed_zton9.value) + parseInt(ed_zton10.value) +
			  parseInt(ed_zton11.value) + parseInt(ed_zton12.value) + parseInt(ed_zton13.value) + parseInt(ed_zton14.value) + parseInt(ed_zton15.value);  



	var tonSum2; // 출발지 상세정보에서 입력된 차량총수량
	tonSum2 = parseInt(grd_list3.GetCellValue(0, 3)) + parseInt(grd_list3.GetCellValue(1, 3)) + parseInt(grd_list3.GetCellValue(2, 3)) + 
				  parseInt(grd_list3.GetCellValue(3, 3)) + parseInt(grd_list3.GetCellValue(4, 3)) + parseInt(grd_list3.GetCellValue(5, 3)) + 
				  parseInt(grd_list3.GetCellValue(6, 3)); 


	if(tonSum1 != tonSum2)	{	
		gfn_showAlert("입력한 톤 정보 총 차량대수와 출발지 상세정보에서 입력된 차량총수량이 일치하지 않습니다.");		
		return;
	}




	//return;
	/*
	저장전 validation 체크	
	*/


	if(length(cal_erdat.Value) == 8)
	{
		tit_clearActionInfo();
		tit_callService(
			"COMM_ACT"
			, "getSdReturnDate"
			, ""
			, ""
			, "I_PSPID="+quote(ds_list2.getColumn(0,"PSPID")) + " I_GUBUN='D' I_DATE="+quote(cal_erdat.Value)
			, "fn_afterDate"
			,true,true,true);
	}


}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterDate(errCode, errMsg) {

	if(E_FLAG == 'P')
	{
		// 배차일자 배차시간 기준 차량 업체 확인 
		ds_chkLifnr.ClearData();
		ds_setLifnr.ClearData();

		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0502001B_S8");
		tit_addSearchActionInfo("mm05:MM0502001B_S10");


		ds_cond2.setColumn(0, "ERDAT", cal_erdat.Value);
		if(me_erzet.Value > 1200)
		{
			ds_cond2.setColumn(0, "APGUBUN", "P");
		}
		else
		{
			ds_cond2.setColumn(0, "APGUBUN", "A");
		}
		tit_callService(
			""
			, ""
			, "ds_cond2=ds_cond2"
			, "ds_chkLifnr=ds_chkLifnr ds_chkLifnrelse=ds_chkLifnrelse"
			, ""
			, "fn_afterDateQuery"
			, true);

	}
	else
	{
		alert("수금전 출하계획을 작성할 수 없습니다.");
		return;
	}	
}



function fn_afterDateQuery()
{
	var edzton1, edzton2, edzton3, edzton4, edzton5, edzton6, edzton7, edzton8, edzton9, edzton10, edzton11, edzton12, edzton13, edzton14, edzton15;
	if(length(trim(ed_zton1.value )) == 0) 	{
		edzton1  = "0";
	}	else 	{
		edzton1  = ed_zton1.value;
	}
	if(length(trim(ed_zton2.value )) == 0) 	{
		edzton2  = "0";
	}	else 	{
		edzton2  = ed_zton2.value;
	}
	if(length(trim(ed_zton3.value )) == 0) 	{
		edzton3  = "0";
	}	else 	{
		edzton3  = ed_zton3.value;
	}
	if(length(trim(ed_zton4.value )) == 0) 	{
		edzton4  = "0";
	}	else 	{
		edzton4  = ed_zton4.value;
	}
	if(length(trim(ed_zton5.value )) == 0) 	{
		edzton5  = "0";
	}	else 	{
		edzton5  = ed_zton5.value;
	}
	if(length(trim(ed_zton6.value )) == 0) 	{
		edzton6  = "0";
	}	else 	{
		edzton6  = ed_zton6.value;
	}
	if(length(trim(ed_zton7.value )) == 0) 	{
		edzton7  = "0";
	}	else 	{
		edzton7  = ed_zton7.value;
	}
	if(length(trim(ed_zton8.value )) == 0) 	{
		edzton8  = "0";
	}	else 	{
		edzton8  = ed_zton8.value;
	}
	if(length(trim(ed_zton9.value )) == 0) 	{
		edzton9  = "0";
	}	else 	{
		edzton9  = ed_zton9.value;
	}
	if(length(trim(ed_zton10.value )) == 0) 	{
		edzton10  = "0";
	}	else 	{
		edzton10  = ed_zton10.value;
	}
	if(length(trim(ed_zton11.value )) == 0) 	{
		edzton11  = "0";
	}	else 	{
		edzton11  = ed_zton11.value;
	}
	if(length(trim(ed_zton12.value )) == 0) 	{
		edzton12  = "0";
	}	else 	{
		edzton12  = ed_zton12.value;
	}
	if(length(trim(ed_zton13.value )) == 0) 	{
		edzton13  = "0";
	}	else 	{
		edzton13  = ed_zton13.value;
	}
	if(length(trim(ed_zton14.value )) == 0) 	{
		edzton14  = "0";
	}	else 	{
		edzton14  = ed_zton14.value;
	}
	if(length(trim(ed_zton15.value )) == 0) 	{
		edzton15  = "0";
	}	else 	{
		edzton15  = ed_zton15.value;
	}	

	var edzton;
		edzton = parseInt(edzton1) + parseInt(edzton2) + parseInt(edzton3) + parseInt(edzton4) + parseInt(edzton5) +
		parseInt(edzton6) + parseInt(edzton7) + parseInt(edzton8) + parseInt(edzton9) + parseInt(edzton10) +
		parseInt(edzton11) + parseInt(edzton12) + parseInt(edzton13) + parseInt(edzton14) + parseInt(edzton15);

	ds_setLifnr.AddRow();
	// ds_setLifnr 설정 	
	ds_setLifnr.setColumn(0, "ERDAT", cal_erdat.Value);
	if(me_erzet.Value > 1200)
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "P");
	}
	else
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "A");
	}



	// 저장 처리 
	tit_clearActionInfo();


	// 출하예정현황 화면에서 중앙운수로 배차 업체 설정시 3038100062
	if(RECEIV_LIFNR == "3038100062" ) 
	{
		ds_setLifnr.SetColumn(0, "LIFNR", "3038100062");
		ds_setLifnr.SetColumn(0, "ZTON", edzton);  // 운송 차량수
		if(ds_chkLifnrelse.rowcount == 0)
		{
			tit_addSingleActionInfo("mm05:MM0502001B_I6");
		}
		else
		{			
			tit_addSingleActionInfo("mm05:MM0502001B_U3");
		}
	}
	else
	{			
		if(ds_chkLifnr.rowcount == 0)
		{				
			ds_setLifnr.SetColumn(0, "LIFNR", "3148131696");	// 운송업체 설정 아무런 데이타가 없는 경우 로지 먼저 
			ds_setLifnr.SetColumn(0, "ZTON", edzton);  // 운송 차량수

			tit_addSingleActionInfo("mm05:MM0502001B_I6");
		}
		else if(ds_chkLifnr.rowcount == 1)
		{
			if( parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) >= 5)	//현재 설정값이 5대 이상인 경우
			{	// 업체를 변경하고 입력된 총 차량수를 설정
				if(ds_chkLifnr.GetColumn(0,"LIFNR") == "3148131696") // 로지이면 신원으로 
				{
					ds_setLifnr.SetColumn(0, "LIFNR", "1018194310");
					ds_setLifnr.SetColumn(0, "ZTON", edzton);  // 운송 차량수

					tit_addSingleActionInfo("mm05:MM0502001B_I6");
				}
				else // 신원이면 로지로 
				{
					ds_setLifnr.SetColumn(0, "LIFNR", "3148131696");
					ds_setLifnr.SetColumn(0, "ZTON", edzton);  // 운송 차량수

					tit_addSingleActionInfo("mm05:MM0502001B_I6");
				}			
			}
			else // 현재 설정값이 5대 미만이면 현재 업체 그대로 차량수는 현재값 + 입력값 
			{		
				ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(0,"LIFNR"));
				ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) + parseInt(edzton) );

				tit_addSingleActionInfo("mm05:MM0502001B_U2");
			}
		}
		else if(ds_chkLifnr.rowcount == 2)
		{
			if( parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) >= parseInt(ds_chkLifnr.GetColumn(1,"ZTON")) )
			{
				ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(1,"LIFNR"));
				ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(1,"ZTON")) + parseInt(edzton) );

				tit_addSingleActionInfo("mm05:MM0502001B_U2");
			}
			else
			{
				ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(0,"LIFNR"));
				ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) + parseInt(edzton) );

				tit_addSingleActionInfo("mm05:MM0502001B_U2");
			}
		}
	}
	/*
	tit_addSelectExActionInfo(
		"mm05:MM0502001B_S9"
	   ,"mm05:MM0502001B_I6"
	   ,"mm05:MM0502001B_U1");
	*/

	if(length(trim(cb_hocha.value)) == 0)
	{
		tit_callService(
			""
			, ""
			, "ds_setLifnr=ds_setLifnr"
			, ""
			, ""
			, "fn_checkLifnr");

	}
	else
	{
		fn_save3();
	}

	//fn_checkLifnr();
}





function fn_checkLifnr()
{	
	fn_save3();
}



/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save3() {

	//Fullset, Fullset(Jamb포함) 여부 파악 2012.07.23 이호영 
	if (ck_fullset.Value == "1" && ck_fullset_1.Value == "1") 
	{
		gfn_showalert("FULL SET, FULL SET(JAMB)중 하나의 값만 체크하십시요");
		return;
	}
	else
	{

		if (ck_fullset.Value == "1")
		{
			ds_list5.SetColumn(0,"ZFULLSET","X");
		}
		if (ck_fullset_1.Value == "1")
		{
			ds_list5.SetColumn(0,"ZFULLSET","Y");
		}        
	}	

	//추가출하사유 입력란 추가 2014.12.30 조영욱
	if ((ds_addr.GetColumn(0,"ZQNTY")) == "01" || (ds_addr.GetColumn(0,"ZQNTY")) == "02")
	{
		if ( cb_text.Value == null )
		{
			//gfn_showalert("추가출하사유를 입력해 주세요");
			ds_list5.SetColumn(0,"ZFULLSET2","1"); //추가출하사유 미입력시 기본 정상출하로 값 세팅
		}
		else
		{
			ds_list5.SetColumn(0,"ZFULLSET2",cb_text.Value);
		}
	}

	if(length(trim(cb_hocha.value)) == 0)
	{
		if(ck_input.Value == "1")
		{
			ed_zton1.value = "0";
			ed_zton2.value = "0";
			ed_zton3.value = "0";
			ed_zton4.value = "0";
			ed_zton5.value = "0";
			ed_zton6.value = "0";
			ed_zton7.value = "0";
			ed_zton8.value = "0";
			ed_zton9.value = "0";
			ed_zton10.value = "0";
			ed_zton11.value = "0";
			ed_zton12.value = "0";
			ed_zton13.value = "0";
			ed_zton14.value = "0";
			ed_zton15.value = "0";			
		}
		else if(ck_input.Value == "0" &&
				(ed_zton1.value == "0" || ed_zton1.value == "") &&
				(ed_zton2.value == "0" || ed_zton2.value == "") &&
				(ed_zton3.value == "0" || ed_zton3.value == "") &&
				(ed_zton4.value == "0" || ed_zton4.value == "") &&
				(ed_zton5.value == "0" || ed_zton5.value == "") &&
				(ed_zton6.value == "0" || ed_zton6.value == "") &&
				(ed_zton7.value == "0" || ed_zton7.value == "") &&
				(ed_zton8.value == "0" || ed_zton8.value == "") &&
				(ed_zton9.value == "0" || ed_zton9.value == "") &&
				(ed_zton10.value == "0" || ed_zton10.value == "") &&
				(ed_zton11.value == "0" || ed_zton11.value == "") &&
				(ed_zton12.value == "0" || ed_zton12.value == "") &&
				(ed_zton13.value == "0" || ed_zton13.value == "") &&
				(ed_zton14.value == "0" || ed_zton14.value == "") &&
				(ed_zton15.value == "0" || ed_zton15.value == ""))
		{
			gfn_showMsg("CW00001",st_ton.Value);
			return;
		}


		// 필수 입력사항 및 일자 체크
		if ( !gfn_checkFromToObj(cal_erdat, cal_dpten, true, st_date.value) )
			return;
		if (cal_erdat.Value == cal_dpten.Value && me_erzet.Value > me_upten.Value)
		{
			gfn_showMsg("CW00022",st_upten.value);
			me_upten.SetFocus();
			return;
		}

		if(ck_input.Value == "0")
		{
			if(length(me_erzet.Value) != 4)
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(me_erzet.Value == "0000" || me_erzet.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(substr(me_erzet.Value,0,2) > "24" || substr(me_erzet.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			if(length(me_upten.Value) != 4)
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(me_upten.Value == "0000" || me_upten.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(substr(me_upten.Value,0,2) > "24" || substr(me_upten.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
		}	

		ds_list5.SetColumn(0,"ZKNUM",cb_hocha.Value);
		ds_list5.SetColumn(0,"BEZEI",cb_plst.text);
	}
	if(length(trim(ed_zton1.value )) == 0) ed_zton1.value  = "0";
	if(length(trim(ed_zton2.value )) == 0) ed_zton2.value  = "0";
	if(length(trim(ed_zton3.value )) == 0) ed_zton3.value  = "0";
	if(length(trim(ed_zton4.value )) == 0) ed_zton4.value  = "0";
	if(length(trim(ed_zton5.value )) == 0) ed_zton5.value  = "0";
	if(length(trim(ed_zton6.value )) == 0) ed_zton6.value  = "0";
	if(length(trim(ed_zton7.value )) == 0) ed_zton7.value  = "0";
	if(length(trim(ed_zton8.value )) == 0) ed_zton8.value  = "0";
	if(length(trim(ed_zton9.value )) == 0) ed_zton9.value  = "0";
	if(length(trim(ed_zton10.value)) == 0) ed_zton10.value = "0";
	if(length(trim(ed_zton11.value)) == 0) ed_zton11.value = "0";
	if(length(trim(ed_zton12.value)) == 0) ed_zton12.value = "0";
	if(length(trim(ed_zton13.value)) == 0) ed_zton13.value = "0";
	if(length(trim(ed_zton14.value)) == 0) ed_zton14.value = "0";
	if(length(trim(ed_zton15.value)) == 0) ed_zton15.value = "0";

	if(ck_trcost.Value == '1') 
	{
		ds_list5.SetColumn(0,"ZTRCOST","X");
	}
	else 
	{
		ds_list5.SetColumn(0,"ZTRCOST","");
	}
	ds_list5.SetColumn(0,"ZTYPE","11");

	if(length(trim(ds_list.getcolumn(0,"VTEXT"))) == 0 && ck_trcost.Value == '0')
	{
		gfn_showMsg("CI00021","운송지역");
		return;
	}
	// 시간
	if(TRIM(ds_list5.getcolumn(0,"ERZET")) > '')
	{
		if (length(TRIM(ds_list5.getcolumn(0,"ERZET"))) == 4)
		{
			ds_list5.SetColumn(0,"ERZET",TRIM(ds_list5.getcolumn(0,"ERZET"))+"00");
		}
		if (length(TRIM(ds_list5.getcolumn(0,"UPTEN"))) == 4)
		{
			ds_list5.SetColumn(0,"UPTEN",TRIM(ds_list5.getcolumn(0,"UPTEN"))+"00");
		}
	}	


	/* 
	출발지 상세 정보 설정 
	*/		
	if( length(trim(grd_list3.GetCellValue(0, 2) )) != 0 && 
		length(trim(grd_list3.GetCellValue(0, 3) )) != 0 &&
		length(trim(grd_list3.GetCellValue(0, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT1", ds_list6.GetColumn(0, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY1", ds_list6.GetColumn(0, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON1", ds_list6.GetColumn(0, "ZTON"));		    
	}else{
				ds_list5.SetColumn(0,"TPLSTDT1", "");
				ds_list5.SetColumn(0,"TPLSTDTQTY1", "");
				ds_list5.SetColumn(0,"TPLSTDTZTON1", "");		    
	}

	if( length(trim(grd_list3.GetCellValue(1, 2) )) != 0 && 
		length(trim(grd_list3.GetCellValue(1, 3) )) != 0 &&
		length(trim(grd_list3.GetCellValue(1, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT2", ds_list6.GetColumn(1, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY2", ds_list6.GetColumn(1, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON2", ds_list6.GetColumn(1, "ZTON"));	  
	}else{
				ds_list5.SetColumn(0,"TPLSTDT2","");
				ds_list5.SetColumn(0,"TPLSTDTQTY2","");
				ds_list5.SetColumn(0,"TPLSTDTZTON2","");		    
	}

	if( length(trim(grd_list3.GetCellValue(2, 2) )) != 0 && 
		length(trim(grd_list3.GetCellValue(2, 3) )) != 0 &&
		length(trim(grd_list3.GetCellValue(2, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT3", ds_list6.GetColumn(2, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY3", ds_list6.GetColumn(2, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON3", ds_list6.GetColumn(2, "ZTON"));	    
	 }else{
				ds_list5.SetColumn(0,"TPLSTDT3","");
				ds_list5.SetColumn(0,"TPLSTDTQTY3","");
				ds_list5.SetColumn(0,"TPLSTDTZTON3","");		    
	 }

	 if( length(trim(grd_list3.GetCellValue(3, 2) )) != 0 && 
		 length(trim(grd_list3.GetCellValue(3, 3) )) != 0 &&
		 length(trim(grd_list3.GetCellValue(3, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT4", ds_list6.GetColumn(3, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY4", ds_list6.GetColumn(3, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON4", ds_list6.GetColumn(3, "ZTON"));		    
	 }else{
				ds_list5.SetColumn(0,"TPLSTDT4","");
				ds_list5.SetColumn(0,"TPLSTDTQTY4","");
				ds_list5.SetColumn(0,"TPLSTDTZTON4","");		    
	 }

	 if( length(trim(grd_list3.GetCellValue(4, 2) )) != 0 && 
		 length(trim(grd_list3.GetCellValue(4, 3) )) != 0 &&
		 length(trim(grd_list3.GetCellValue(4, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT5", ds_list6.GetColumn(4, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY5", ds_list6.GetColumn(4, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON5", ds_list6.GetColumn(4, "ZTON"));    
	 }	else{
				ds_list5.SetColumn(0,"TPLSTDT5","");
				ds_list5.SetColumn(0,"TPLSTDTQTY5","");
				ds_list5.SetColumn(0,"TPLSTDTZTON5","");		    
	 }

	 if( length(trim(grd_list3.GetCellValue(5, 2) )) != 0 && 
		 length(trim(grd_list3.GetCellValue(5, 3) )) != 0 &&
		 length(trim(grd_list3.GetCellValue(5, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT6", ds_list6.GetColumn(5, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY6", ds_list6.GetColumn(5, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON6", ds_list6.GetColumn(5, "ZTON"));    
	 }	else{
				ds_list5.SetColumn(0,"TPLSTDT6","");
				ds_list5.SetColumn(0,"TPLSTDTQTY6","");
				ds_list5.SetColumn(0,"TPLSTDTZTON6","");		    
	 }

	 if( length(trim(grd_list3.GetCellValue(6, 2) )) != 0 && 
		 length(trim(grd_list3.GetCellValue(6, 3) )) != 0 &&
		 length(trim(grd_list3.GetCellValue(6, 4) )) != 0 )	    {
			ds_list5.SetColumn(0,"TPLSTDT7", ds_list6.GetColumn(6, "BEZEI_NM"));
			ds_list5.SetColumn(0,"TPLSTDTQTY7", ds_list6.GetColumn(6, "CAREACH"));
			ds_list5.SetColumn(0,"TPLSTDTZTON7", ds_list6.GetColumn(6, "ZTON"));    
	 }	else{
				ds_list5.SetColumn(0,"TPLSTDT7","");
				ds_list5.SetColumn(0,"TPLSTDTQTY7","");
				ds_list5.SetColumn(0,"TPLSTDTZTON7","");		    
	 }


	//도착지정보 PARENT에 세팅
	ds_lzone.setColumn(0,"LZONE",cb_area.Value);

	// 배송업체  설정
	if(length(trim(cb_hocha.value)) == 0)
	{
		ds_list5.SetColumn(0,"LIFNR",ds_setLifnr.GetColumn(0, "LIFNR"));
	}
	else
	{
		ds_list5.SetColumn(0,"LIFNR",INIT_LIFNR);
	}




	fn_save2();
	fn_close();
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	gfn_insertEmptyRow( ds_hocha, G_CODE_ALL );
	cb_hocha.Index = 0;

	cb_lifnr.Index = 0;
	ds_hyun.Filter("LIFNR=="+ds_lifnr.GetColumn(0,"CODE"));
	cb_hyun.Index = 0;

	cb_area.Value = ds_list2.GetColumn(0,"LZONE");


	if (ds_hocha.GetColumn(0,"ZFULLSET") == "X")
	{
		ck_fullset.Value = "1";
	}
	if (ds_hocha.GetColumn(0,"ZFULLSET") == "Y")
	{
		ck_fullset_1.Value = "1";
	}

	fn_defartSetup();


}
function fn_defartSetup()
{
	ds_list6.Copy(dslist6_Copy);
	grd_list3.BindDataset = "ds_list6";

	// 출발지 상세정보 첫번째 컬럼 출발지 세팅 
	if ( isExistVar("vstel", true) ) {	 

		if(vstel == "1100") // 천안 
			cVstel = "0000008502"; 
		else if(vstel == "1000") // 이천 
			cVstel = "0000008501";
		else if(vstel == "1200") // 충주
			cVstel = "0000008503";
		else if(vstel == "1300") // 진천  
			cVstel = "0000008504";
	   ds_list6.SetColumn(0,"BEZEI_NM", cVstel);
	}
}

/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterHocha(errCode, errMsg) {
	gfn_insertEmptyRow( ds_hocha, G_CODE_ALL );
	cb_hocha.Index = 0;
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	grd_list3.BindDataset= "ds_list6";
	dslist5_copy.ClearData();
	ds_list7.ClearData();
	ds_list7.AddRow();

	var arr = Split(ds_list4.GetColumn(0, "TPLSTDTQTY1"), ",");						
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI1ZTON"+(parseInt(j)+1),arr[j]);						
	}

	var arr2 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY2"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI2ZTON"+(parseInt(j)+1),arr2[j]);			
	}

	var arr3 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY3"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI3ZTON"+(parseInt(j)+1),arr3[j]);			
	}

	var arr4 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY4"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI4ZTON"+(parseInt(j)+1),arr4[j]);			
	}

	var arr5 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY5"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI5ZTON"+(parseInt(j)+1),arr5[j]);		
	}

	var arr6 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY6"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI6ZTON"+(parseInt(j)+1),arr6[j]);		
	}

	var arr7 = Split(ds_list4.GetColumn(0, "TPLSTDTQTY7"), ",");				
	for ( var j = 0; j< 15; j++)	{
		ds_list7.setColumn(0,"BEZEI7ZTON"+(parseInt(j)+1),arr7[j]);		
	}

	INIT_LIFNR = ds_list5.getColumn(0,"LIFNR"); // 호차 선택시 운송업체 			



}

