		ed_zton13.Enable = false;
		ed_zton14.Enable = false;
		ed_zton15.Enable = false;
		grd_list3.Enable = false;
		btn_Getton.Enable = false;
		ds_list6.SetColumn(0,"BEZEI_NM", "");
	}
	else
	{
		ed_zton1.Enable = true;
		ed_zton2.Enable = true;
		ed_zton3.Enable = true;
		ed_zton4.Enable = true;
		ed_zton5.Enable = true;
		ed_zton6.Enable = true;
