		cb_hyun.Enable = true;
		cal_erdat.Enable = true;
		cal_dpten.Enable = true;
		me_erzet.Enable = true;
		me_upten.Enable = true;
		cb_plst.Enable = true;
		ed_ztext.Enable = true;
		grd_list3.Enable = true;
		btn_Getton.Enable = true;
		ds_list4.ClearData();
		ds_list7.ClearData();		
		ds_list7.AddRow();
		dslist5_copy.ClearData();
		fn_defartSetup();

		ed_zton1.value = "";
		ed_zton2.value = "";
		ed_zton3.value = "";
		ed_zton4.value = "";
		ed_zton5.value = "";
		ed_zton6.value = "";
