		cb_hyun.index = 0;
    	cal_erdat.value = gfn_getAfterDate(gfn_getCurrDate(),1);
    	cal_dpten.value = gfn_getAfterDate(gfn_getCurrDate(),1);
		me_erzet.value = "";
		me_upten.value = "";
		cb_plst.index = 0;
		ed_ztext.value = "";


	}
}
/********************************************************************************
* 기      능   :  배차일자 변경시
********************************************************************************/
function cal_erdat_OnChanged(obj,strOldText,strNewText)
{
	ed_zton1.SetFocus();
	if(length(trim(obj.value)) != 8) return;
	fn_validDate(strNewText);
}
/********************************************************************************
* 기      능   :  배차일자 focus이동시
********************************************************************************/
function cal_erdat_OnKillFocus(obj)
{
	if(length(trim(obj.value)) != 8) return;
	else fn_validDate(obj.value);
}
/********************************************************************************
* 기      능   :
********************************************************************************/
function fn_validDate(date_)
{
	fn_getHocha(date_);
}


