function ck_InputYn_OnClick(obj,strValue)
{
	if(ck_InputYn.Value == "0")
	{
		grd_list3.Enable = false;
	}
	else
	{
		grd_list3.Enable = true;
	}
}



function fn_chgSum(nRow)
{

	var arr = Split(ds_list6.GetColumn(nRow, "CAREACH"), ",");

	var sum1;
	for ( var i = 0; i < arr.length(); i ++ ) {
	//trace(arr[i]);	
		sum1 += (parseInt(arr[i]));
	}
	if(sum1 == "0"){
		sum1 = '';
	}	
	ds_list6.setColumn(nRow,'CARQT',sum1);	

}


function fn_setton()
