function btn_Getton_OnClick(obj)
{
	if(	length(trim(ed_zton1.value)) == 0  && length(trim(ed_zton2.value)) == 0 
		&& length(trim(ed_zton3.value)) == 0 && length(trim(ed_zton4.value)) == 0 
		&& length(trim(ed_zton5.value)) == 0 && length(trim(ed_zton6.value)) == 0 
		&& length(trim(ed_zton7.value)) == 0 && length(trim(ed_zton8.value)) == 0 
		&& length(trim(ed_zton9.value)) == 0 && length(trim(ed_zton10.value)) == 0 
		&& length(trim(ed_zton11.value)) == 0 && length(trim(ed_zton12.value)) == 0 
		&& length(trim(ed_zton13.value)) == 0 && length(trim(ed_zton14.value)) == 0 
		&& length(trim(ed_zton15.value)) == 0 )		
	 {
		gfn_showalert("상단에 입력된 톤수 정보가 없습니다.");
		return;	
	 }	
	 else{
		fn_setton();	
	 }


}

