/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	if(ds_gubun.rowcount == 0)
	{
		// 메시지 처리 
		gfn_showMsg("CI00002",ds_gubun.RowCount()+"");
		return;
	}
	else
	{
		gubun = ds_gubun.GetColumn(0,"POTYPE");

		fn_query2();
	}
}
function fn_afterQuery2(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(grd_list, errCode, errMsg);

	if(gubun == "X") //box일 경우 
		ds_list2.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(0,"MATNR"))) +" && POSID2 == "+quote(TRIM(ds_list.GetColumn(0,"POSID"))));
	else if(gubun == "") //item일 경우
		ds_list2.Filter("MATNR=="+quote(TRIM(ds_list.GetColumn(0,"MATNR"))) );
	ds_list2.row = -1;	
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
trace("저장후 호출 ");
trace(ds_list5.SaveXML());
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
