/********************************************************************************
* 기      능   : 헤더 클릭시 디테일 FILTERING해준다.
********************************************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	if(gubun == "X") //box일 경우 
		ds_list2.Filter("EXTWG=="+quote(TRIM(ds_list.GetColumn(nRow,"MATNR"))) +" && POSID2 == "+quote(TRIM(ds_list.GetColumn(nRow,"POSID"))));
	else if(gubun == "") //item일 경우
		ds_list2.Filter("MATNR=="+quote(TRIM(ds_list.GetColumn(nRow,"MATNR"))) );
	ds_list2.row = -1;		
}
/********************************************************************************
* 기      능   : 상태가 '0'인 경우 check box disable해준다.
********************************************************************************/
function fn_disable(ZSTAT)
{
	if(ZSTAT == '0' || ZSTAT == 'C') return "none";
	else return "checkbox";
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && ds_list.GetColumn(nRow,"ZSTAT") == "1")
			{
				ds_list.SetColumn(nRow,"FLAG","U");
				ds_list.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"CHK","0");
			}
		break;
	}	
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && ds_list2.GetColumn(nRow,"ZSTAT") == "1" )
			{
				ds_list2.SetColumn(nRow,"FLAG","U");
				ds_list2.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list2.SetColumn(nRow,"FLAG","");
				ds_list2.SetColumn(nRow,"CHK","0");
			}
		break;
	}
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 리스트의 폰트 색깔 처리
********************************************************************************/
function ufn_setColor(obj,stat,colyn)
{
	cValue = decode(stat,'1','default','0','blue');
	if(colyn == "Y")
	{
		cValue = 'red';
	}
	//선택된 로우의 Font Color
	obj.SelColor = cValue;
	//Grid의 Font Color
	return cValue;
}
