function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);


	ed_pjt.SetFocus();
	cb_box.Index = 0;
	cb_lifnr.Index = 0;


	// 공급 업체명 설정 
	// 그룹 권한 변경으로 인한 예외처리 추가 - 20180816 LCH
	user_grp = gfn_getUserGroup();
	if(user_grp == '10' || G_USER_ID == '7001155' || G_USER_ID == '7001186' || G_USER_ID == 'M1349A01' || G_USER_ID == '7000759' || G_USER_ID == '7000282') 
	{
		cb_lifnr.Value = G_VEND_CODE;
		cb_lifnr.Enable = false;
	} 

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0502003A_S2");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_sdvali_cd=ds_sdvali_cd"
        , ""
        , "fn_cdAfterQuery"
        , true);

	gfn_showMsg("CI00007");
	//저장시간이 오래걸려서 저장중에 다시 저장버튼 클릭하는것을 방지
	btn_save.Enable = false;
}

