/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query() {
	var arr = "";
	if(gfn_isNull(ed_pjt, true, st_pjt.value))
		return;
	if(length(trim(ed_pjt.value)) < 6)
	{
		arr = [st_pjt.value, 6];
		gfn_showMsg("CW00174",arr);
		ed_pjt.SetFocus();
		return;
	}

	if(length(trim(ed_matnr.value)) > 0 && length(trim(ed_matnr.value)) < 3)
	{
		arr = [st_matnr.value, 3];
		gfn_showMsg("CW00174",arr);
		ed_matnr.SetFocus();
		return;
	}

	// sort mark 삭제 
	gfn_clearGridSortMark(grd_list);
	ds_list.row = -1;
	grd_list.SetCellProp("head", 0, "Text", 0);

	tit_clearActionInfo();

	if(substr(trim(ed_pjt.Value),6,2) == "NC")
	{
		tit_addSearchActionInfo("mm05:MM0502003A_S3");
	} else {
		tit_addSearchActionInfo("mm05:MM0502003A_S1");
	}

	if(length(trim(ed_pjt.value)) < 6)
	{
		arr = [st_pjt.value, 6];
		gfn_showMsg("CW00174",arr);
		ed_pjt.SetFocus();
		return;
	}

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}

	var err[2];
	for (var i=0; i<ds_list.rowcount(); i++)
	{
		if(trim(ds_list.getcolumn(i,"GUBN")) == "B") {
			alert("건산법 문제로 출하가 차단 된 프로젝트입니다.\n출하팀에 문의 바랍니다.");
			return;
		}

		if(ds_sdvali_cd.searchRow("CODE== " + quote(ds_list.GetColumn(i,"POSID"))) > -1)
		{
			err[0] = ds_list.GetColumn(i,"POSID");
		    err[1] = "영업 출고보류";
			gfn_showAlert("CW00193",err);
			return;
		}
	}

	var arg = "fa_pjt="+quote(ed_pjt.Text);
	if(user_grp == '10') 
	{
		arg += " fa_jamb="+quote(ds_lifnr.GetColumn(ds_lifnr.FindRow("CODE",G_VEND_CODE),"JAMB"));
	} else {
		arg += " fa_jamb=N";
	}
	gfn_openPopup("MM0502003B", true, arg);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save2() {
	if ( !gfn_isChangeDs(ds_list, true, "")){
		return;
	}
	//저장시간이 오래걸려서 저장중에 다시 저장버튼 클릭하는것을 방지
	btn_save.Enable = false;

	ds_list2.ClearData();

	var nRow = 0;
	for( var i=0; i<ds_list.rowcount; i++ )
	{
		if (ds_list.GetColumn(i,"FLAG") == "U")
		{
			ds_list2.AddRow();

			ds_list2.SetColumn(nRow,"MANDT",ds_list.GetColumn(i,"MANDT"));
			ds_list2.SetColumn(nRow,"ZKNUM",ds_list.GetColumn(i,"ZKNUM"));
			ds_list2.SetColumn(nRow,"ZKNUM2",ds_list.GetColumn(i,"ZKNUM2"));
			ds_list2.SetColumn(nRow,"PSPID",ds_list.GetColumn(i,"PSPID"));
			ds_list2.SetColumn(nRow,"EBELN",ds_list.GetColumn(i,"EBELN"));
			ds_list2.SetColumn(nRow,"EBELP",ds_list.GetColumn(i,"EBELP"));
			ds_list2.SetColumn(nRow,"POSID",ds_list.GetColumn(i,"POSID"));
			ds_list2.SetColumn(nRow,"POST1",ds_list.GetColumn(i,"POST1"));
			ds_list2.SetColumn(nRow,"KUNWE",ds_list.GetColumn(i,"KUNWE"));
			ds_list2.SetColumn(nRow,"TEXT30",ds_list.GetColumn(i,"TEXT30")); //ZLZONE
			ds_list2.SetColumn(nRow,"BLOCK",ds_list.GetColumn(i,"BLOCK"));
			ds_list2.SetColumn(nRow,"MATNR",ds_list.GetColumn(i,"MATNR"));
			ds_list2.SetColumn(nRow,"MATKL",ds_list.GetColumn(i,"MATKL"));
			ds_list2.SetColumn(nRow,"MATNR_NM",ds_list.GetColumn(i,"MATNR_NM")); //MAKTX
			if (length(trim(ds_list.GetColumn(i,"ERFMG"))) == 0)
				ds_list2.setcolumn(nRow,"ERFMG","0");
			else
				ds_list2.SetColumn(nRow,"ERFMG",ds_list.GetColumn(i,"ERFMG"));
			ds_list2.SetColumn(nRow,"ERFME",ds_list.GetColumn(i,"ERFME"));

			ds_list2.SetColumn(nRow,"KUNNR",ds_list.GetColumn(i,"KUNNR")); //납품처
			ds_list2.SetColumn(nRow,"NAME1",ds_list.GetColumn(i,"NAME1"));  //현장명
			ds_list2.SetColumn(nRow,"ZLZONE",ds_lzone.GetColumn(0,"LZONE")); //도착지
			ds_list2.SetColumn(nRow,"ZZTEMNO",ds_list.GetColumn(i,"ZZTEMNO")); //설치팀
			ds_list2.SetColumn(nRow,"ZZTEAMJ",ds_list.GetColumn(i,"ZZTEAMJ")); //설치팀명
			ds_list2.SetColumn(nRow,"ZLZONE_NM",ds_list.GetColumn(i,"ZLZONE_NM")); //도착지명

			ds_list2.SetColumn(nRow,"FLAG","U");

			nRow++;
		} //if	
	} //for

	if(ds_list2.rowcount == 0) return;

	if(length(trim(ds_list5.getcolumn(0,"TPLST"))) == 0 )
	{
		gfn_showMsg("CI00021","출발지");
		return;
	}

	tit_clearActionInfo();
	tit_callService(
        "MM0502003A_ACT"
        , "save"
        , "ds_list=ds_list2 ds_list2=ds_list5"
        , ""
        , ""
        , "fn_afterSave");
}
