function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술
	gfn_initForm();

	ds_list5.ClearData();
	ds_list5.addrow();

	gfn_initDs(ds_date_, true);
	ck_input.value = 0;

	cal_erdat.value = gfn_getAfterDate(gfn_getCurrDate(),1);
	cal_dpten.value = gfn_getAfterDate(gfn_getCurrDate(),1);

	if (isExistVar("fa_jamb", true) && fa_jamb == "Y")
	{
		ck_trcost.Value = '1';
		ck_trcost.Enable = false;
	} else if(gfn_getUserGroup() == "10") {
		//fn_close();
	}

	ds_plst.Filter("LIFNR=="+quote(trim(ds_list.getcolumn(0,"KUNNR"))));
	cb_plst.index = 0;

	var fa_pspid = ds_list.getcolumn(0,"POSID");
	if (substr(fa_pspid,0,2) == "QM")
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,2,6));
	}
	else if (substr(fa_pspid,0,1) == "Q")
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,1,6));
	}
	else
	{
		ds_date_.SetColumn(0,"PSPID",substr(fa_pspid,0,6));
	}

	//도착지명 display
	//ed_area.value = ds_list.getcolumn(0,"ZLZONE_NM");

	ds_date_.SetColumn(0,"HOCHA",gfn_getAfterDate(gfn_getCurrDate(),1));

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0502003B_S1", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S2", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S3", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S5", null, true);
	tit_addSearchActionInfo("mm05:MM0502001B_S6", null, true);

	tit_callService(
        ""
        , ""
        , "ds_date_=ds_date_"
        , "ds_hocha=ds_hocha ds_lifnr=ds_lifnr ds_hyun=ds_hyun ds_addr=ds_addr ds_area=ds_area"
        , ""
        , "fn_afterLifnr"
        , true);
}
