function fn_getHocha(hocha)
{
	ds_date_.SetColumn(0,"HOCHA",hocha);

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0502003B_S1", null, true);

	tit_callService(
        ""
        , ""
        , "ds_date_=ds_date_"
        , "ds_hocha=ds_hocha"
        , ""
        , "fn_afterHocha"
        , true);
}
/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {	

	if(length(trim(cb_hocha.value)) == 0)
	{
		if(ck_input.Value == "1")
		{

		}
		else if(ck_input.Value == "0" &&
				(ed_zton1.value == "0" || ed_zton1.value == "") &&
				(ed_zton2.value == "0" || ed_zton2.value == "") &&
				(ed_zton3.value == "0" || ed_zton3.value == "") &&
				(ed_zton4.value == "0" || ed_zton4.value == "") &&
				(ed_zton5.value == "0" || ed_zton5.value == "") &&
				(ed_zton6.value == "0" || ed_zton6.value == "") &&
				(ed_zton7.value == "0" || ed_zton7.value == "") &&
				(ed_zton8.value == "0" || ed_zton8.value == "") &&
				(ed_zton9.value == "0" || ed_zton9.value == "") &&
				(ed_zton10.value == "0" || ed_zton10.value == "") &&
				(ed_zton11.value == "0" || ed_zton11.value == "") &&
				(ed_zton12.value == "0" || ed_zton12.value == "") &&
				(ed_zton13.value == "0" || ed_zton13.value == "") &&
				(ed_zton14.value == "0" || ed_zton14.value == "") &&
				(ed_zton15.value == "0" || ed_zton15.value == ""))
		{
			gfn_showMsg("CW00001",st_ton.Value);
			return;
		}

		// 필수 입력사항 및 일자 체크
		if ( !gfn_checkFromToObj(cal_erdat, cal_dpten, true, st_date.value) )
			return;
		if (cal_erdat.Value == cal_dpten.Value && me_erzet.Value > me_upten.Value)
		{
			gfn_showMsg("CW00022",st_upten.value);
			me_upten.SetFocus();
			return;
		}

		if(ck_input.Value == "0")
		{
			if(length(me_erzet.Value) != 4)
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(me_erzet.Value == "0000" || me_erzet.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			else if(substr(me_erzet.Value,0,2) > "24" || substr(me_erzet.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_erzet.value);
				me_erzet.SetFocus();
				return;
			}
			if(length(me_upten.Value) != 4)
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(me_upten.Value == "0000" || me_upten.Value > "2400" )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
			else if(substr(me_upten.Value,0,2) > "24" || substr(me_upten.Value,2,2) > 59 )
			{
				gfn_showMsg("CW00022",st_upten.value);
				me_upten.SetFocus();
				return;
			}
		}
	}




	if(length(trim(cb_area.Value)) == 0 && ck_trcost.Value == '0')
	{
		gfn_showMsg("CI00021","운송지역");
		return;
	}








	if(length(cal_erdat.Value) == 8)
	{
		tit_clearActionInfo();
		tit_callService(
			"COMM_ACT"
			, "getSdReturnDate"
			, ""
			, ""
			, "I_PSPID="+quote(ds_list.getcolumn(0,"PSPID")) + " I_GUBUN='D' I_DATE="+quote(cal_erdat.Value)
			, "fn_afterDate"
			,true,true,true);
	}
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterDate(errCode, errMsg) {
	if(E_FLAG == 'P')
	{
		//fn_save3();
		// 배차일자 배차시간 기준 차량 업체 확인 
		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0502001B_S8");


		ds_cond2.setColumn(0, "ERDAT", cal_erdat.Value);
		if(me_erzet.Value > 1200)
		{
			ds_cond2.setColumn(0, "APGUBUN", "P");
		}
		else
		{
			ds_cond2.setColumn(0, "APGUBUN", "A");
		}
		tit_callService(
			""
			, ""
			, "ds_cond2=ds_cond2"
			, "ds_chkLifnr=ds_chkLifnr"
			, ""
			, "fn_afterDateQuery"
			, true);
	}
	else
	{
		alert("수금전 출하계획을 작성할 수 없습니다.");
		return;
	}
}


function fn_afterDateQuery()
{
