	if(length(trim(ed_zton1.value )) == 0) 	{
		edzton1  = "0";
	}	else 	{
		edzton1  = ed_zton1.value;
	}
	if(length(trim(ed_zton2.value )) == 0) 	{
		edzton2  = "0";
	}	else 	{
		edzton2  = ed_zton2.value;
	}
	if(length(trim(ed_zton3.value )) == 0) 	{
		edzton3  = "0";
	}	else 	{
		edzton3  = ed_zton3.value;
	}
	if(length(trim(ed_zton4.value )) == 0) 	{
		edzton4  = "0";
	}	else 	{
		edzton4  = ed_zton4.value;
	}
	if(length(trim(ed_zton5.value )) == 0) 	{
		edzton5  = "0";
	}	else 	{
		edzton5  = ed_zton5.value;
	}
	if(length(trim(ed_zton6.value )) == 0) 	{
		edzton6  = "0";
	}	else 	{
		edzton6  = ed_zton6.value;
	}
	if(length(trim(ed_zton7.value )) == 0) 	{
		edzton7  = "0";
	}	else 	{
		edzton7  = ed_zton7.value;
	}
	if(length(trim(ed_zton8.value )) == 0) 	{
		edzton8  = "0";
	}	else 	{
		edzton8  = ed_zton8.value;
	}
	if(length(trim(ed_zton9.value )) == 0) 	{
		edzton9  = "0";
	}	else 	{
		edzton9  = ed_zton9.value;
	}
	if(length(trim(ed_zton10.value )) == 0) 	{
		edzton10  = "0";
	}	else 	{
		edzton10  = ed_zton10.value;
	}
	if(length(trim(ed_zton11.value )) == 0) 	{
		edzton11  = "0";
	}	else 	{
		edzton11  = ed_zton11.value;
	}
	if(length(trim(ed_zton12.value )) == 0) 	{
		edzton12  = "0";
	}	else 	{
		edzton12  = ed_zton12.value;
	}
	if(length(trim(ed_zton13.value )) == 0) 	{
		edzton13  = "0";
	}	else 	{
		edzton13  = ed_zton13.value;
	}
	if(length(trim(ed_zton14.value )) == 0) 	{
		edzton14  = "0";
	}	else 	{
		edzton14  = ed_zton14.value;
	}
	if(length(trim(ed_zton15.value )) == 0) 	{
		edzton15  = "0";
	}	else 	{
		edzton15  = ed_zton15.value;
	}	

	var edzton;
		edzton = parseInt(edzton1) + parseInt(edzton2) + parseInt(edzton3) + parseInt(edzton4) + parseInt(edzton5) +
		parseInt(edzton6) + parseInt(edzton7) + parseInt(edzton8) + parseInt(edzton9) + parseInt(edzton10) +
		parseInt(edzton11) + parseInt(edzton12) + parseInt(edzton13) + parseInt(edzton14) + parseInt(edzton15);

	// ds_setLifnr 설정 
	ds_setLifnr.setColumn(0, "ERDAT", cal_erdat.Value);
	if(me_erzet.Value > 1200)
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "P");
	}
	else
	{
		ds_setLifnr.setColumn(0, "APGUBUN", "A");
	}			


	// 저장 처리 
	tit_clearActionInfo();

	if(ds_chkLifnr.rowcount == 0)
	{	
		ds_setLifnr.SetColumn(0, "LIFNR", "3148131696");	// 운송업체 설정 아무런 데이타가 없는 경우 로지 먼저 
		ds_setLifnr.SetColumn(0, "ZTON", edzton);  // 운송 차량수

		tit_addSingleActionInfo("mm05:MM0502001B_I6");
	}
	else if(ds_chkLifnr.rowcount == 1)
	{
		if( parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) >= 5)	//현재 설정값이 5대 이상인 경우
		{	// 업체를 변경하고 입력된 총 차량수를 설정
			if(ds_chkLifnr.GetColumn(0,"LIFNR") == "3148131696") // 로지이면 신원으로 
			{
				ds_setLifnr.SetColumn(0, "LIFNR", "1018194310");
				ds_setLifnr.SetColumn(0, "ZTON", edzton);

				tit_addSingleActionInfo("mm05:MM0502001B_I6");
			}
			else // 신원이면 로지로 
			{
				ds_setLifnr.SetColumn(0, "LIFNR", "3148131696");
				ds_setLifnr.SetColumn(0, "ZTON", edzton);

				tit_addSingleActionInfo("mm05:MM0502001B_I6");
			}			
		}
		else // 현재 설정값이 5대 미만이면 현재 업체 그대로 차량수는 현재값 + 입력값 
		{		
			ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(0,"LIFNR"));
			ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) + parseInt(edzton) );

			tit_addSingleActionInfo("mm05:MM0502001B_U2");
		}
	}
	else if(ds_chkLifnr.rowcount == 2)
	{
		if( parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) >= parseInt(ds_chkLifnr.GetColumn(1,"ZTON")) )
		{
			ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(1,"LIFNR"));
			ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(1,"ZTON")) + parseInt(edzton) );

			tit_addSingleActionInfo("mm05:MM0502001B_U2");
		}
		else
		{
			ds_setLifnr.SetColumn(0, "LIFNR", ds_chkLifnr.GetColumn(0,"LIFNR"));
			ds_setLifnr.SetColumn(0, "ZTON", parseInt(ds_chkLifnr.GetColumn(0,"ZTON")) + parseInt(edzton) );

			tit_addSingleActionInfo("mm05:MM0502001B_U2");
		}
	}




	/*
	tit_addSelectExActionInfo(
		"mm05:MM0502001B_S9"
	   ,"mm05:MM0502001B_I6"
	   ,"mm05:MM0502001B_U1");
	*/

	if(length(trim(cb_hocha.value)) == 0)
	{
		tit_callService(
			""
			, ""
			, "ds_setLifnr=ds_setLifnr"
			, ""
			, ""
			, "fn_checkLifnr");

	}	
	else
	{
		fn_save3();
	}

	//fn_checkLifnr();
}





