		if (length(TRIM(ds_list5.getcolumn(0,"ERZET"))) == 4)
		{
			ds_list5.SetColumn(0,"ERZET",TRIM(ds_list5.getcolumn(0,"ERZET"))+"00");
		}
		if (length(TRIM(ds_list5.getcolumn(0,"UPTEN"))) == 4)
		{
			ds_list5.SetColumn(0,"UPTEN",TRIM(ds_list5.getcolumn(0,"UPTEN"))+"00");
		}
	}	
	//도착지정보 PARENT에 세팅
	ds_lzone.setColumn(0,"LZONE",cb_area.Value);

	// 배송업체  설정
	if(length(trim(cb_hocha.value)) == 0)
	{
		ds_list5.SetColumn(0,"LIFNR",ds_setLifnr.GetColumn(0, "LIFNR"));
	}	
	else
	{
		ds_list5.SetColumn(0,"LIFNR",INIT_LIFNR);
	}

	fn_save2();
	fn_close();
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	gfn_insertEmptyRow( ds_hocha, G_CODE_ALL );
	cb_hocha.Index = 0;

	cb_lifnr.Index = 0;
	ds_hyun.Filter("LIFNR=="+ds_lifnr.GetColumn(0,"CODE"));
	cb_hyun.Index = 0;

	cb_area.Value = ds_list.GetColumn(0,"ZLZONE");
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterHocha(errCode, errMsg) {
	gfn_insertEmptyRow( ds_hocha, G_CODE_ALL );
	cb_hocha.Index = 0;
}
/********************************************************************************
* 기      능   : 조회 Callback
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
	INIT_LIFNR = ds_list5.getColumn(0,"LIFNR"); // 호차 선택시 운송업체 
}
