/********************************************************************************
* 기      능   :  호차정보 변경시
********************************************************************************/
function cb_hocha_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(trim(strCode) > '')
	{
		ed_zton1.Enable = false;
		ed_zton2.Enable = false;
		ed_zton3.Enable = false;
		ed_zton4.Enable = false;
		ed_zton5.Enable = false;
		ed_zton6.Enable = false;
		ed_zton7.Enable = false;
		ed_zton8.Enable = false;
		ed_zton9.Enable = false;
		ed_zton10.Enable = false;
		ed_zton11.Enable = false;
		ed_zton12.Enable = false;
		ed_zton13.Enable = false;
		ed_zton14.Enable = false;
		ed_zton15.Enable = false;
		ck_input.Enable = false;
		if (isExistVar("fa_jamb", true) && fa_jamb == "Y")
	    {
		   ck_trcost.Enable = false;
		} else {
			ck_trcost.Enable = true;
		}
		cb_lifnr.Enable = false;
		cb_hyun.Enable = false;
		cal_erdat.Enable = false;
		cal_dpten.Enable = false;
		me_erzet.Enable = false;
		me_upten.Enable = false;
		cb_plst.Enable = false;
		ed_ztext.Enable = true;

		tit_clearActionInfo();
		tit_addSearchActionInfo("mm05:MM0502003B_S2", null, true);

		tit_callService(
			""
			, ""
			, ""
			, "ds_list5=ds_list5"
			, "ZKNUM="+quote(strCode)
			, "fn_afterQuery"
			, true);
	}
	else
	{
		ed_zton1.Enable = true;
		ed_zton2.Enable = true;
		ed_zton3.Enable = true;
		ed_zton4.Enable = true;
		ed_zton5.Enable = true;
		ed_zton6.Enable = true;
		ed_zton7.Enable = true;
		ed_zton8.Enable = true;
		ed_zton9.Enable = true;
		ed_zton10.Enable = true;
		ed_zton11.Enable = true;
		ed_zton12.Enable = true;
		ed_zton13.Enable = true;
		ed_zton14.Enable = true;
		ed_zton15.Enable = true;
		ck_input.Enable = true;
		if (isExistVar("fa_jamb", true) && fa_jamb == "Y")
	    {
		   ck_trcost.Enable = false;
			ck_trcost.Value = "1";
		} else {
			ck_trcost.Enable = true;
			ck_trcost.Value = "";
		}
		cb_lifnr.Enable = true;
		cb_hyun.Enable = true;
		cal_erdat.Enable = true;
		cal_dpten.Enable = true;
		me_erzet.Enable = true;
		me_upten.Enable = true;
		cb_plst.Enable = true;
		ed_ztext.Enable = true;

		ed_zton1.value = "";
		ed_zton2.value = "";
		ed_zton3.value = "";
		ed_zton4.value = "";
		ed_zton5.value = "";
		ed_zton6.value = "";
		ed_zton7.value = "";
		ed_zton8.value = "";
		ed_zton9.value = "";
		ed_zton10.value = "";
		ed_zton11.value = "";
		ed_zton12.value = "";
		ed_zton13.value = "";
		ed_zton14.value = "";
		ed_zton15.value = "";
		ck_input.value = "";
		cb_lifnr.index = 0;
		cb_hyun.index = 0;
    	cal_erdat.value = gfn_getAfterDate(gfn_getCurrDate(),1);
    	cal_dpten.value = gfn_getAfterDate(gfn_getCurrDate(),1);
		me_erzet.value = "";
		me_upten.value = "";
		cb_plst.index = 0;
		ed_ztext.value = "";
	}
}
/********************************************************************************
* 기      능   :  배차일자 변경시
********************************************************************************/
function cal_erdat_OnChanged(obj,strOldText,strNewText)
{
	ed_zton1.SetFocus();
	fn_validDate(strNewText);
}
/********************************************************************************
* 기      능   :  배차일자 focus이동시
********************************************************************************/
function cal_erdat_OnKillFocus(obj)
{
	if(length(trim(obj.value)) != 8) return;
	else fn_validDate(obj.value);
}
/********************************************************************************
* 기      능   :
********************************************************************************/
function fn_validDate(date_)
{
	fn_getHocha(date_);
}
