/********************************************************************************
* 기      능   : 상태가 '3','5','6','7','8'인 경우 check box disable해준다.
********************************************************************************/
function fn_disable(ZSTAT4)
{
	if(ZSTAT4 == '3' || ZSTAT4 == '4' || ZSTAT4 == '5' || ZSTAT4 == '7' || ZSTAT4 == '8' || ZSTAT4 == '9' || ZSTAT4='C') return "none";
	else return "checkbox";
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && (ds_list.GetColumn(nRow,"ZSTAT4") == "1" || ds_list.GetColumn(nRow,"ZSTAT4") == "2" ||ds_list.GetColumn(nRow,"ZSTAT4") == "6" ))
			{
				ds_list.SetColumn(nRow,"FLAG","U");
				ds_list.SetColumn(nRow,"CHK","1");
			}
			else
			{
				ds_list.SetColumn(nRow,"FLAG","");
				ds_list.SetColumn(nRow,"CHK","0");
			}
		break;
	}	
	obj.fireevent = true;
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CHK":
			if( varNewValue == "1" && (ds_list2.GetColumn(nRow,"ZSTAT4") == "1" || ds_list2.GetColumn(nRow,"ZSTAT4") == "2" ||ds_list2.GetColumn(nRow,"ZSTAT4") == "6" ))
			{
				ds_list2.SetColumn(nRow,"FLAG","U");
				ds_list2.SetColumn(nRow,"CHK","1");

				// 전기일자 입력
				btn_div.cal_micldt.value = gfn_getCurrDate();
			}
			else
			{
				ds_list2.SetColumn(nRow,"FLAG","");
				ds_list2.SetColumn(nRow,"CHK","0");

				// 전기일자 입력
				btn_div.cal_micldt.value = "";
			}
		break;
	}
	obj.fireevent = true;

	// Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	if ( strColumnID == "LFIMG") 
	{
			obj.FireEvent = false;
		    var meval = toNumber(obj.GetColumn(nRow,"ERFMG")) - toNumber(obj.GetColumn(nRow,"ENMNG"));

			if ( ds_list2.GetOrgColumn(nRow,"LFIMG") == varNewValue ) {
    			obj.SetColumn(nRow,"CHK", "0");
				obj.FireEvent = true;
				return;
			}
			else if ( toNumber(varNewValue) > toNumber(meval) || toNumber(varNewValue) <= 0)
			{
			   var arr = ["등록수량은 0 이상의 값으로", "소요수량과 기출고수량의 차"];
			   gfn_showAlert("CW00015",arr);
			   obj.SetColumn(nRow, "LFIMG", varOldValue);
			   obj.SetColumn(nRow,"CHK", "0");
	 		   obj.FireEvent = true;
			   return;
			}

			obj.FireEvent = true;

			obj.SetColumn(nRow, "CHK", "1");
	}
}
/********************************************************************************
* 기      능   : 리스트의 폰트 색깔 처리
********************************************************************************/
function ufn_setColor(obj,stat4)
{
	cValue = decode(stat4,'1','default','2','default','6','default','3','red','4','blue','5','blue','7','blue','8','blue','9','blue','C','red');
	//선택된 로우의 Font Color
	obj.SelColor = cValue;
	//Grid의 Font Color
	return cValue;
}
