/********************************************************************************
* 기      능   :  미출하자재 저장
********************************************************************************/
function btn_div_btn_save_OnClick(obj)
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    // 자료가 없으면 리턴
	if (ds_list2.rowcount < 1){
		gfn_showAlert("CW00010");
		return;
	}
    //변경값 확인
	if ( !gfn_isChangeDs(ds_list2, true) ) {
		return;
	}

	for (var i=0;i < ds_list2.rowcount; i++)
	{
		if (ds_list2.GetColumn(i,"FLAG") != "" )
		{
			// 제품명
			if ( length(ds_list2.GetColumn(i,"LFIMG")) < 1) {
				gfn_showMsg("CW00001","등록수량");
				return;
			}

			if (ds_list2.GetColumn(i,"VBELN") = "") {
				alert("미출하일자 입력이 필요 합니다.");
				return;
			}


		}
	}

	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	tit_clearActionInfo();
	tit_addSaveActionInfo(
			""
			, "mm05:MM0502005A_I1"
			, ""
			, "FLAG"
			,""
			, 0
			, "");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");
}

function fn_afterSave(errCode, errMsg)
{
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}
