{
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 : 프로젝트인 경우는 기간 무시
	if (length(ed_pjt.Text) < 6)
	{
		if ( gfn_isNull(cal_budat_fm, true) ) 
		{
			gfn_showMsg("CW00001",st_budat.Text+"(시작)");
			return;
		}
		if ( gfn_isNull(cal_budat_to, true) ) 
		{
			gfn_showMsg("CW00001",st_budat.Text+"(종료)");
			return;
		}
		// 조회기간: 1개월 제한
		if ( ds_cond.GetColumn(0,"BUDAT_FM") <> ds_cond.GetColumn(0,"BUDAT_TO")
		     && length(ds_cond.GetColumn(0,"MATNR")) > 0
			 && gfn_getMonthCnt(ds_cond.GetColumn(0,"BUDAT_FM"), ds_cond.GetColumn(0,"BUDAT_TO")) > 3 )
		{
			gfn_showMsg("CW00078"," 3 ");
			cal_budat_fm.SetFocus();
			return;
		}
		else if ( ds_cond.GetColumn(0,"BUDAT_FM") <> ds_cond.GetColumn(0,"BUDAT_TO")
		     && length(ds_cond.GetColumn(0,"MATNR")) < 1
			 && gfn_getMonthCnt(ds_cond.GetColumn(0,"BUDAT_FM"), ds_cond.GetColumn(0,"BUDAT_TO")) > 1 )
		{
			gfn_showMsg("CW00078"," 1 ");
			cal_budat_fm.SetFocus();
			return;
		}
	}
	// sql 
	ufn_SetCond();
	// HTTP SESSION TIMEOUT을 변경
	http.TimeOut = 1800;

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0503001A_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true);
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}

/********************************************************************************
* 기      능   : 액셀  
********************************************************************************/
function fn_excel() 
{
	// Excel Export 
	gfn_exportExcel(grd_list,G_USER_ID);
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	// HTTP TIMEOUT 설정
    http.TimeOut = F_TIMEOUT;

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) 
	{
		ufn_enableBtn("excel", true);
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
	} 
	else 
	{
		ufn_enableBtn("excel", false);
//		cal_rdate_p.Enable = false;
		gfn_showMsg("CW00010");
	}
}
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_cdAfterQuery(errCode, errMsg) 
{
	ds_ekgrp_cd.insertRow(0);
	ds_ekgrp_cd.setColumn(0, "CODE", "");
	ds_ekgrp_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	ds_matkl_cd.insertRow(0);
	ds_matkl_cd.setColumn(0, "CODE", "");
	ds_matkl_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	gfn_copyDs(ds_group_cd,ds_group_cd_0);

	ds_group_cd.insertRow(0);
	ds_group_cd.setColumn(0, "CODE", "");
	ds_group_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	//  발주담당자
	cb_ekgrp.Index = 0;
	//  자재그룹
	cb_matkl.Index = 0;
	//  자재관리 담당자
	cb_group.Index = 0;
}

//=========================================================================================
// [ PART 5 ]
// Detail Process Event 정의 ( User Define function )
// 사용자 정의 함수는 ufn_xxxx() 형태로 작성 
//=========================================================================================
/********************************************************************************
* 기      능   :  조회 조건 생성
********************************************************************************/
function ufn_SetCond() 
{
	var sqlstr = " WHERE MS.MANDT ='" + G_MANDT + "'"         
               + " AND MS.WERKS ='1000'"  
               + " AND MS.BWART ='101'"   
               //+ " AND MS.MBLNR LIKE '_%'"   
               //+ " AND MK.MBLNR LIKE '_%'"   
               //+ " AND MS.BWART ='101'"   
               //+ " AND MS.LGORT BETWEEN '2310' AND '2330'";
               + " AND MS.LGORT BETWEEN '2310' AND '2510'"; // 20091022 이은기 차장님요청에 의해 2,3공장추가
    // 자재                         
	// if (ds_cond.GetColumn(0, "MATNR") <> '')
	// {
	  // sqlstr += " AND MS.MATNR LIKE '" + ds_cond.GetColumn(0, "MATNR") + "%'"; 
	// }	
	// 기간
	if (length(ds_cond.GetColumn(0, "POSID")) < 6)
	{
		sqlstr += " AND MK.MANDT ='" + G_MANDT + "'"
		        + " AND MK.BUDAT BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "'";
	}
	// 프로젝트	  
	if (ds_cond.GetColumn(0, "POSID") <> '')
	{
	  sqlstr += " AND PS.MANDT ='" + G_MANDT + "'"
			  + " AND PS.POSID LIKE '" + ds_cond.GetColumn(0, "POSID") + "%'"; 
	}

	sqlstr += " AND RE.MANDT ='" + G_MANDT + "'";
	if (ds_cond.GetColumn(0, "MATNR") <> '')
	{
	  sqlstr += " AND RE.MATNR LIKE '" + ds_cond.GetColumn(0, "MATNR") + "%'"; 
	}
	if (ds_cond.GetColumn(0, "EKGRP") <> '')
	{
	  sqlstr += " AND RE.EKGRP = '" + ds_cond.GetColumn(0, "EKGRP") + "'"; 
	}
	if (ds_cond.GetColumn(0, "MATKL") <> '')
	{
	  sqlstr += " AND RE.MATKL = '" + ds_cond.GetColumn(0, "MATKL") + "'"; 
	}

	// 조건이 없을 경우 자재 문서 번호 조회
	if (length(ds_cond.GetColumn(0, "POSID")) == 0 && length(ds_cond.GetColumn(0, "MATNR")) == 0)
	{
		sqlstr += " AND MS.MBLNR LIKE '_%'"
		        + " AND MK.MBLNR LIKE '_%'" ;
	}

	// 테코구분 20100219 테코 처리던 자재에 대해서는 조회 안함
	if (ds_cond.GetColumn(0, "GUBN") == '1')
	{
	  sqlstr += " AND MA2.TXT04 NOT IN ('E','F','Z') "; 
	} else if(ds_cond.GetColumn(0, "GUBN") == '2') {
	  sqlstr += " AND MA2.TXT04 IN ('E','F','Z') "; 
	}
	//  자재 관리 담당자 구분 20100219 테코 처리된 자재에 대해서는 조회 안함
	if (ds_cond.GetColumn(0, "GROUP") <> '')
	{
	  sqlstr += " AND M1.C_GROUP = '" + ds_cond.GetColumn(0, "GROUP") + "'"; 
	}
	sqlstr += "  AND MSG.SMBLN IS NULL";

	ds_cond.setColumn(0, "SQLSTR", sqlstr);
}

//=========================================================================================
// [ PART 6 ]
// component Event 정의 : 각 Component 별 이벤트 
//=========================================================================================
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} 
	else 
	{
		// 그리드 정렬 처리 
		gfn_multiSortGrid(obj, false, 1);
	}
}
/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.value);
	gfn_openPopup("CS9601002A",true,arg);
}
/********************************************************************************
* 기      능   : 자재번호 팝업
********************************************************************************/
function btn_matnr_OnClick(obj)
{
	var matnr = gfn_openPopup("MM0901002A",true); 

	if (matnr !=null && obj == btn_matnr) 
	{
		ed_matnr.value = matnr;
	}
}

/********************************************************************************
* 기      능   : 영역 지정 복사
* 
* 전제조건 : ColSelect Property 값이 True이고 
*            MultiSelect Property 값이 False 일 경우에만 
*            AreaSelect Property 값이 True로 사용할 수 있음
********************************************************************************/
var strSeparator = "	";

function grd_list_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) 
	{
		grd_fn_ClipboardCopy(obj, "	");
	} 
	// Ctrl + V
	else if(bCtrl == 1 && nChar == 86) 
	{
		grd_fn_ClipboardPaste(obj, "	");
	} 
	// Ctrl + Z
	else if(bCtrl == 1 && nChar == 90) 
	{
		grd_fn_CellReset(obj);
	}
}
