var F_TIMEOUT = 0;
var budat_fm;
var budat_to;
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) 
{
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	// HTTP TIMEOUT 설정
	F_TIMEOUT = HTTP.TimeOut;

	// 구분
	cb_kzear.Index = 2;
	//기간
	cal_budat_fm.value = gfn_getBeforeDate(gfn_getCurrDate(), 6);
	cal_budat_to.value = gfn_getCurrDate();

	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0503001A_S2");
	tit_addSearchActionInfo("mm05:MM0503001A_S3");
	tit_addSearchActionInfo("mm05:MM0503001A_S4");

	tit_callService(
        ""
        , ""
        , ""
        , "ds_ekgrp_cd=ds_ekgrp_cd ds_matkl_cd=ds_matkl_cd ds_group_cd=ds_group_cd"
        , ""
        , "fn_cdAfterQuery"
        , true);

    // 엑셀 버튼 비활성화
	ufn_enableBtn("excel", false);
	ufn_enableBtn("print", false);

	gfn_showMsg("CI00007");
}
