/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 : 프로젝트인 경우는 기간 무시
	if (length(ed_pjt.Text) < 6)
	{
		if ( gfn_isNull(cal_budat_fm, true) ) 
		{
			gfn_showMsg("CW00001",st_budat.Text+"(시작)");
			return;
		}
		if ( gfn_isNull(cal_budat_to, true) ) 
		{
			gfn_showMsg("CW00001",st_budat.Text+"(종료)");
			return;
		}
		// 조회기간: 1개월 제한
		if ( ds_cond.GetColumn(0,"BUDAT_FM") <> ds_cond.GetColumn(0,"BUDAT_TO")
		     && length(ds_cond.GetColumn(0,"MATNR")) > 0
			 && gfn_getMonthCnt(ds_cond.GetColumn(0,"BUDAT_FM"), ds_cond.GetColumn(0,"BUDAT_TO")) > 3 )
		{
			gfn_showMsg("CW00078"," 3 ");
			cal_budat_fm.SetFocus();
			return;
		}
		else if ( ds_cond.GetColumn(0,"BUDAT_FM") <> ds_cond.GetColumn(0,"BUDAT_TO")
		     && length(ds_cond.GetColumn(0,"MATNR")) < 1
			 && gfn_getMonthCnt(ds_cond.GetColumn(0,"BUDAT_FM"), ds_cond.GetColumn(0,"BUDAT_TO")) > 1 )
		{
			gfn_showMsg("CW00078"," 1 ");
			cal_budat_fm.SetFocus();
			return;
		}
	}
	// sql 
	ufn_SetCond();
	// HTTP SESSION TIMEOUT을 변경
	http.TimeOut = 3600;

	tit_clearActionInfo();	
	tit_addSearchActionInfo("mm05:MM0503002A_S1");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"
		, ""
		, "fn_afterQuery"
		, true);
}
/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() 
{
	close();
}
