{
	// Excel Export 
	gfn_exportExcel(grd_list,G_USER_ID);
}
//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	// HTTP TIMEOUT 설정
    http.TimeOut = F_TIMEOUT;

    // 조회 결과가 있으면 엑셀 버튼 활성화
	if ( ds_list.rowCount() > 0 ) 
	{
		ufn_enableBtn("excel", true);
		ufn_enableBtn("print", true);
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
	} 
	else 
	{
		ufn_enableBtn("excel", false);
		ufn_enableBtn("print", false);
//		cal_rdate_p.Enable = false;
		gfn_showMsg("CW00010");
	}
	budat_fm = ds_cond.GetColumn(0,"BUDAT_FM");
	budat_to = ds_cond.GetColumn(0,"BUDAT_TO");
}

function fn_cdAfterQuery(errCode, errMsg) 
{
	ds_ekgrp_cd.insertRow(0);
	ds_ekgrp_cd.setColumn(0, "CODE", "");
	ds_ekgrp_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	ds_matkl_cd.insertRow(0);
	ds_matkl_cd.setColumn(0, "CODE", "");
	ds_matkl_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	gfn_copyDs(ds_group_cd,ds_group_cd_0);

	ds_group_cd.insertRow(0);
	ds_group_cd.setColumn(0, "CODE", "");
	ds_group_cd.setColumn(0, "CODE_NAME", "- 전 체 -");

	//  발주담당자
	cb_ekgrp.Index = 0;
	//  자재그룹
	cb_matkl.Index = 0;
	//  자재관리 담당자
	cb_group.Index = 0;
}

