/********************************************************************************
* 기      능   :  조회 조건 생성
********************************************************************************/
function ufn_SetCond() 
{
	var sqlstr  = " WHERE PS.MANDT ='" + G_MANDT + "'";
	sqlstr += " AND P34.MANDT = '" + G_MANDT + "'"; //WALLBOX
    sqlstr += " AND P34.ITEMNO = 'E322A'"; //WALLBOX
	// 프로젝트
	if (ds_cond.GetColumn(0, "POSID") <> '')
	{
	  //sqlstr += " AND PS.POSID LIKE '" + ds_cond.GetColumn(0, "POSID") + "%'"; 
	  sqlstr += " AND P34.POSID LIKE '" + ds_cond.GetColumn(0, "POSID") + "%'"; //WALLBOX
	}
	sqlstr += " AND PS.ZZSHIP1 LIKE '_%'";
	sqlstr += " AND PS.STUFE = 2";

	//WALLBOX출하실적일 기준으로 내역 조회 진석희cj, 박병운gj요청 2012.07.16 이호영 처리
	if (length(ds_cond.GetColumn(0, "POSID")) < 6) 
	{
	   sqlstr += " AND P34.PPIDAT BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "'";		
	}
	// 기간
/*	if (length(ds_cond.GetColumn(0, "POSID")) < 6) 
	{
		sqlstr += " AND ("		
				+ " PS.ZZPRO01 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "' OR"
				+ " PS.ZZPRO02 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "' OR"
				+ " PS.ZZPRO03 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "' OR" 
				+ " PS.ZZPRO04 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "' OR"
				+ " PS.ZZPRO05 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "' OR"
				+ " PS.ZZPRO06 BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "'"		
				+ " )";
	}	*/

	sqlstr += " AND RE.MANDT = '" + G_MANDT + "'"
		    + " AND RE.BWART = '281'"
		    + " AND RE.WERKS = '1000'"
		    + ds_cond.GetColumn(0, "KZEAR")
		    + " AND RE.XLOEK = ''"
		    + " AND RE.BAUGR = ''"
		    + " AND RE.XWAOK = 'X'";
	// 기간 
/*	if (length(ds_cond.GetColumn(0, "POSID")) < 6)
	{
		sqlstr += " AND ("
				+ " CASE CASE WHEN M6.BOXNO IS NULL THEN '1' ELSE SUBSTR(M6.BLOCK,1,1) END" 
				+ "      WHEN '1' THEN PS.ZZPRO01"
				+ "      WHEN '2' THEN CASE WHEN PS.ZZPRO02='00000000' THEN PS.ZZPRO01 ELSE PS.ZZPRO02 END" 
				+ "      WHEN '3' THEN CASE WHEN PS.ZZPRO03='00000000' THEN PS.ZZPRO01 ELSE PS.ZZPRO03 END"
				+ "      WHEN '4' THEN CASE WHEN PS.ZZPRO04='00000000' THEN PS.ZZPRO01 ELSE PS.ZZPRO04 END"
				+ "      WHEN '5' THEN CASE WHEN PS.ZZPRO05='00000000' THEN PS.ZZPRO01 ELSE PS.ZZPRO05 END"
				+ "      WHEN '6' THEN CASE WHEN PS.ZZPRO06='00000000' THEN PS.ZZPRO01 ELSE PS.ZZPRO06 END"    
				+ "      ELSE PS.ZZPRO01 END" 
				+ " ) BETWEEN '" + ds_cond.GetColumn(0, "BUDAT_FM") + "' AND '" + ds_cond.GetColumn(0, "BUDAT_TO") + "'";
	}*/

	if (ds_cond.GetColumn(0, "MATNR") <> '')
	{
	  sqlstr += " AND RE.MATNR LIKE '" + ds_cond.GetColumn(0, "MATNR") + "%'"; 
	}
	if (ds_cond.GetColumn(0, "EKGRP") <> '')
	{
	  sqlstr += " AND RE.EKGRP = '" + ds_cond.GetColumn(0, "EKGRP") + "'"; 
	}
	if (ds_cond.GetColumn(0, "MATKL") <> '')
	{
	  sqlstr += " AND RE.MATKL = '" + ds_cond.GetColumn(0, "MATKL") + "'"; 
	}
	if (ds_cond.GetColumn(0, "BOX") <> '')
	{
	  sqlstr += " AND CASE WHEN MA.EXTWG='' THEN 'BOX33' ELSE MA.EXTWG END = '" + ds_cond.GetColumn(0, "BOX") + "'"; 
	}
	//  자재 관리 담당자 구분 20100415 
	if (ds_cond.GetColumn(0, "GROUP") <> '')
	{
	  sqlstr += " AND MA.C_GROUP = '" + ds_cond.GetColumn(0, "GROUP") + "'"; 
	}
	//sqlstr += "  AND MSG.SMBLN IS NULL";

	ds_cond.setColumn(0, "SQLSTR", sqlstr);
}

