/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - 자재 상세 조회입력,Flag 처리 
********************************************************************************/

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(ds_list.GetColumn(nRow, "FLAG") != "I")
		ds_list.SetColumn(nRow, "FLAG", "U");


  if ( strColumnID <> "CHECK"){
	ds_list.fireevent = false;
	var colid = strColumnID;

	if ( Trim(obj.GetColumn(nRow,colid)) != Trim(ds_mm109.GetColumn(nRow,colid)))
	{
		obj.SetColumn(nRow,"CHECK","1");

	} else
	{
		obj.SetColumn(nRow,"CHECK","0");
	}

	ds_list.fireevent = true;

    }

}

/********************************************************************************
* 기      능   : 달력
********************************************************************************/
var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = rect[1] +60 ;
	cal_temp.Left = rect[0];
	cal_temp.Visible = true;


	switch ( nCell )
	{

		case 2 : 
			cal_temp.Value = ds_list.GetColumn(nRow, "SSC_DTM1");
			col = "SSC_DTM1";
		break;

		case 11 :
			cal_temp.Value = ds_list.GetColumn(nRow, "INP_DTM");
			col = "INP_DTM";
		break;

		case 9 :
			cal_temp.Value = ds_list.GetColumn(nRow, "COMP_DTM");
			col = "COMP_DTM";
		break;
		/*
	case 12 :
			cal_temp.Value = ds_list.GetColumn(nRow, "CHK_DTM");
			col = "CHK_DTM";
		break;
		*/

		case 13 :
			cal_temp.Value = ds_list.GetColumn(nRow, "SSC_DTM2");
			col = "SSC_DTM2";
		break;

		case 12 :
			cal_temp.Value = ds_list.GetColumn(nRow, "SSC_DTM3");
			col = "SSC_DTM3";
		break;

		case 14 :
			cal_temp.Value = ds_list.GetColumn(nRow, "SSC_DTM4");
			col = "SSC_DTM4";
		break;

	}

	cal_temp.SetFocus();

}
