/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterPlanSave(errCode, errMsg) {

	if ( errCode!= 0 ) {
		gfn_showAlert("CE00001");
		fn_query();
	} else {
		gfn_showMsg("CI00003");

		tit_clearActionInfo();

		tit_addMultiActionInfo("mm05:MM0504002A_U2");

		tit_callService(
			""
			, ""
			, "ds_list2=ds_list2:A"
			, ""
			, ""
			, "fn_afterSMSSave"
			,true);
	}
}

