/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_zzactss.value = "";	
	if(userGroup == '40')
	{
		cb_zzactss.value = "";
	}
	else if(userGroup == '20' || userGroup == '30')
	{
		cb_zzactss.Value = G_AREA_CODE_E;
		if(G_USER_GROUP_E == 'E20' || G_USER_GROUP_E == 'E30')
		{
			cb_zzactss.Enable = true;
		}	
	}
	else
	{
		cb_zzactss.value = "";
		cb_zzactss.Enable = true;
	}
}
