/********************************************************************************
* 기      능   : 그리드 Edit여부를 사용자에 따라서 변경 처리
********************************************************************************/
function setGrdEdit(objGrd) {

		objGrd.Redraw=false;
		for (var j = 0; j < dsArr.length; j++ )
		{
			objGrd.SetCellProp('body',dsArr[j],"ExpandShow","false");
			objGrd.SetCellProp('body',dsArr[j],"edit","none");
		}
		objGrd.Redraw=true;
	// }
}
