/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	ufn_clearInput();
	tit_clearActionInfo();
	tit_addSearchActionInfo("mm05:MM0504003A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond1=ds_cond1"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);


}
/********************************************************************************
* 기      능   : 삽입 
********************************************************************************/
function fn_input() 
{
	var cnt = ds_list.GetRowCount();

	ds_list.AddRow();
	ds_list.setColumn(ds_list.row, "FLAG", "I");
	ds_list.RowPos = cnt;	
}

/********************************************************************************
* 기      능   : 저장(입력) 버튼 클릭
********************************************************************************/
function fn_save() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

    //변경값 확인
	if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}

	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	ds_list2.ClearData();
	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	if (ds_list2.GetRowCount() < 1)
	{
		gfn_showAlert("CW00180","체크표시");
		return;
	}

	/*for (var i=0; i < ds_list2.GetRowCount(); i++)
	{
		if(length(ds_list2.GetColumn(i,"SSC_DTM3")) > 8) {
			gfn_showAlert("CW00001", "미출하일");
			return;	
		}
		if(length(ds_list2.GetColumn(i,"LDESC")) < 1) {

			gfn_showAlert("CW00001", "지연사유");
			return;	
		}
		if(length(ds_list2.GetColumn(i,"DETAIL")) < 1) {
			gfn_showAlert("CW00001", "상세내역");
			return;
		}
		if(length(ds_list2.GetColumn(i,"DEPT")) < 1) {
			gfn_showAlert("CW00001", "원인부서");
			return;
		}
		if(length(ds_list2.GetColumn(i,"PROC_YN")) < 1) {
			gfn_showAlert("CW00001", "처리여부");
			return;
		}
	}*/

	tit_clearActionInfo();
	tit_addSaveActionInfo(
        "",
        "mm05:MM0504003A_U1",
        "",
        "FLAG",
        "",
        0,
        "");

	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2"
        , ""
        , ""
        , "fn_afterSave");

}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}

	for(var i=0; i < ds_list.rowcount; i++)
		{
			if ( ds_list.GetColumn(i,"CHECK") == "1")
			{
				ds_list.SetColumn(i,"CHECK","0");
				return;
			}

		}
}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {

	if(ds_list.GetRowCount() < 1)
	{
		gfn_showMsg("CW00013");
		return;
	}
	if ( !gfn_showConfirm("CI00010") ) 
	{
		return;
	}

	ds_list.fireevent = false;

	ds_list2.ClearData();
	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	if (ds_list2.GetRowCount() < 1) 
	{
		gfn_showAlert("CW00180","체크표시");
		ds_list.fireevent = true;
		return;
	}

	ds_list.fireevent = true;

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm05:MM0504002A_D1");

	tit_callService(
        ""
        , ""
        , "ds_list2=ds_list2"
        , ""
        , ""
        , "fn_afterDelete"
        , true);


}

/********************************************************************************
* 기      능   : 삭제  Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {

	if ( errCode!= 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");
		fn_query();
	}
}




