* 기      능   : 복원 
********************************************************************************/
function fn_reset() {
	if (!gfn_showConfirm("CI00013","작업")) {
		return;
	}	
	ds_list.ClearData();
	ds_cond.ClearData();
	ds_cond.AddRow();
	fn_OnLoadCompleted();
}
