/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) {
    // 구매담당자, 생산관리팀 숨김 200423 yj

    if(g_user_id != "2022532") {
		fn_hide_grid_column("USERID");
		fn_hide_grid_column("PSNO");
		//grd_list.SetColProp(31,"width",0);
		//grd_list.SetColProp(32,"width",0);
	}

	if(G_VEND_NAME != "Smart Factory팀" && G_VEND_NAME != "생산관리팀" && G_VEND_NAME != "출하팀" && G_VEND_NAME != "생산혁신팀" &&
	   G_VEND_NAME != "생산기술팀" && G_VEND_NAME != "생산팀" && G_VEND_NAME != "설계1팀" && G_VEND_NAME != "설계2팀" &&
	   G_VEND_NAME != "설계3팀" && G_VEND_NAME != "Cost ENG팀" && G_VEND_NAME != "Cost Engineering팀" &&
	   G_VEND_NAME != "전략구매팀" && G_VEND_NAME != "구매운영팀" && g_user_id != "2022532") {
	    fn_hide_grid_column("GUBN");
		fn_hide_grid_column("FACSALFAI");
		fn_hide_grid_column("PROPLFAI");
		fn_hide_grid_column("ADELCOST");
	}

	if(G_VEND_NAME = "출하팀" || g_user_id = "2022532"){
		grd_list.Editable = true;
		ufn_enableBtn("save", true);		//저장
		btn_planSMS.visible = true;
		btn_carSMS.visible = true;
		btn_planMAIL.visible = true;
	} else if(G_VEND_NAME = "구매운영팀" || G_VEND_NAME = "생산관리팀" || 
		g_user_id = "2004062" || g_user_id = "3000621" || g_user_id = "3000613" ||
		g_user_id = "2010852" || g_user_id = "2008521" || g_user_id = "3000389" ||
		g_user_id = "2004062" || g_user_id = "3000621" || g_user_id = "2023601" || // 생산팀 담당
		g_user_id = "2015129" || g_user_id = "2023806" || g_user_id = "2028352" ||
		g_user_id = "2023032" || g_user_id = "2025868" || g_user_id = "3014754") { // 생산관리팀 담당
		grd_list.Editable = true;
		btn_schSAVE.visible = true;
	} else if(G_DPT1 == "E1" || G_DPT1 == "E2" || G_DPT1 == "E4" || G_DPT1 == "EI" || G_DPT1 == "EJ" || 
	            G_DPT1 == "EU" || G_DPT1 == "EH" ) { // 설치본부 
		grd_list.Editable = true;
		//btn_schSAVE.visible = true;
		btn_SerChiSAVE.Visible = true;
	}


   	if ( ds_list.rowCount() > 0 ) {
		gfn_copyDs(ds_list, ds_mm109,);
		gfn_showMsg("CI00002", ds_list.rowCount() + "");
	} else {
		gfn_showMsg("CW00010");
	}

	// 예정일 버튼 활성화 21.08.11
	if(g_user_id = "2022532") {
		btn_schSAVE.visible = true;
	}

}



