    if (ds_list2.rowcount < 1)
    {
		gfn_showAlert("CW00013");
		return;
    }

    //저장확인
	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	ds_emtran_0.ClearData();
	var cellp = "";
	//var gbn = "Q";
	var j = 0;
	var msg = "";

	for (var i = 0 ; i < ds_list2.rowcount ; i++ )
