					+ " MESGTITL=" + quote( title ) 
					+ " MESGBODY=" + quote( body ) 
				, ""  //"fn_afterQuery"
				, false
				, true);				
			//=====================메일끝====================
	}

	ds_emtran.ClearData();
	gfn_copyDs(ds_emtran_0, ds_emtran, "GBN = 1");

	tit_clearActionInfo();

	tit_callService(
        "PDA_ACT"
        , ""
        , "ds_emtran=ds_emtran:A"
        , ""
        , ""
        , "fn_afterPlanMAILSave"
        , true
        , true
        , true);
}

function fn_afterPlanMAILSave(errCode, errMsg) {

	if ( errCode!= 0 ) {
		gfn_showAlert("CE00001");
		fn_query();
	} else {
		gfn_showMsg("CI00003");

		tit_clearActionInfo();

		tit_addMultiActionInfo("mm05:MM0504003A_U2");

		tit_callService(
			""
			, ""
			, "ds_list2=ds_list2:A"
			, ""
			, ""
			, "fn_afterSMSSave"
			, true);
	}
}

function fn_hide_grid_column(colid) {
    //grd_list.SetCellProp("head",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	//grd_list.SetCellProp("body",	grd_list.GetBindCellIndex("body",colid),"Display","null");
	grd_list.SetColProp(grd_list.GetBindCellIndex("body",colid),"Width",0);
}

