/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {

	// 체크한 거래box를 새로운 dataset에 담기
	ds_list_0.ClearData();

	ds_list.fireevent = false;
	// 저장할 데이터셋을 복사
	 gfn_copyDs(ds_list, ds_list_0, "CHECK=1");

	ds_list.fireevent = true;

	if (ds_list_0.RowCount() < 1) {
		return;
	}

	if (!gfn_showConfirm("CI00008")) {
		ds_list_0.ClearData();
		return;
	}

	tit_clearActionInfo();

	// 저장 처리 
	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"mm07:MM0701006A_S3"
	   ,"mm07:MM0701006A_I1"
	   ,"mm07:MM0701006A_U1");

//   call 서비스에서 commit실행하여 commit 필요없음.

	tit_callService(
        ""
        , ""
        , "ds_list=ds_list_0:A"
        , ""
        , ""
        , "fn_afterSave");
}
