function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		return;
	} else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_multiSortGrid(obj, false, 1);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}



