	ed_posid = hogin[0];
}

var col = "";
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	// var rect = obj.GetCellRect(nRow, ncell);
	// cal_temp.top = rect[1] +100 ;
	// cal_temp.Left = rect[0]; 
	// cal_temp.Visible = true;

	if(nCell = 19 || nCell = 20 || nCell = 21 || nCell = 22 || nCell = 23 || nCell = 24 || nCell = 26)
	{
			var rect = obj.GetCellRect(nRow, ncell);
			cal_temp.top = rect[1] +60 ;
			cal_temp.Left = rect[0];
			cal_temp.Visible = true;

			switch( nCell )
			{
				case 19:
					cal_temp.Value = ds_list.GetColumn(nRow, "COMP_DAT");
					col = "COMP_DAT";
				break;

				case 20:
					cal_temp.Value = ds_list.GetColumn(nRow, "DOJ_DAT");
					col = "DOJ_DAT";
				break;

				case 21:
					cal_temp.Value = ds_list.GetColumn(nRow, "DOJ_DAT_CP");
					col = "DOJ_DAT_CP";
				break;

				case 22:
					cal_temp.Value = ds_list.GetColumn(nRow, "HIG_DAT_IN");
					col = "HIG_DAT_IN";
				break;
				case 23:
					cal_temp.Value = ds_list.GetColumn(nRow, "HIG_DAT");
					col = "HIG_DAT";
				break;
				case 24:
					cal_temp.Value = ds_list.GetColumn(nRow, "SHIP_SCH");
					col = "SHIP_SCH";
				break;
				case 25:
					cal_temp.Value = ds_list.GetColumn(nRow, "INST_DAT");
					col = "INST_DAT";
				break;		
			}

		cal_temp.SetFocus();

	 }

	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	ds_list.setColumn(ds_list.row, col, strText);

	col = "";
	obj.Visible = false;
}
/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
	col = "";
}

function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (ds_list.GetColumn(nRow,strColumnID) == ds_list.GetOrgBuffColumn(nRow,strColumnID))
	{
		return;
	}

	ds_list.fireevent = false;




	ds_list.fireevent = true;
}

function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//보고서 유형 항목 클릭 시
	if(nCell==18){
		var rep = ds_list.GetColumn(nRow, "REP");
		var pjt = ds_list.GetColumn(nRow, "PJT");
		var hno = ds_list.GetColumn(nRow, "HNO");

		fn_goNextForm(rep,pjt,hno);
	}		
}


function fn_goNextForm(rep,pjt,hno){
	var formID="";

	switch (rep) {
		case 'H':
		case 'W':
			formID = "PS1101003B";
			break;			
		case 'J':
		case 'G':
			formID = "PS1102003B";
			break;			
		case 'S':
			formID = "PS1102001B";
			break;
		case 'C':
			formID = "PS1101007B";
			break;
		case 'D':
			formID = "PS1101010B";
			break;
		case 'F':
			formID = "PS1101009B";
			break;
		case 'R':
			formID = "PS1101005B";
			break;
		case 'N':
			formID = "PS1102006A";
			break;
		case 'X':
			formID = "PS1101011B";
			break;
		case 'Y':
			formID = "PS1101012B";
			break;

		case 'NH':
			formID = "PS1107001B";
			break;		

		case 'NX':
			formID = "PS1107002B";
			break;	

		case 'NC':
			formID = "PS1107003B";
			break;	

		case 'NR':
			formID = "PS1107004B";
			break;

		case 'NY':
			formID = "PS1109001B";
			break;

		case 'NZ':
			formID = "PS1110001B";
			break;

	}

	var arg = "PRE_PJT="+quote(pjt);
		arg += " PRE_HNO="+quote(hno);

	SELFLG = "N";
	gfn_openPopup(formID, true, arg);
	if(SELFLG == "Y") fn_query();
}


