/********************************************************************************
* 기      능   : 엑셀업로드   
********************************************************************************/
function btn_upload_OnClick(obj)
{
	grd_list.Format = "Upload";

	Create("FileDialog", "_fileDialog_", "");
	var dialogObj = object("_fileDialog_");

	dialogObj.Type = "Open";
	dialogObj.FileName = "";
	dialogObj.Filter = "Microsoft Excel File(*.xls)|*.xls|";
	dialogObj.open();

	if (length(dialogObj.FileName) == 0) {
		Destroy( "_fileDialog_" );
		return;
	}

	var sFileName = dialogObj.FilePath + "\\" + dialogObj.FileName;

	ext_ExcelImportByIndex(sFileName,null,"ds_excel",0,1);

	grd_list.BindDataset = "";

	ufn_ExcelToList();

	grd_list.BindDataset = "ds_list";

	Destroy( "_fileDialog_" );  // 불러오기후 dialog 제거	
}

function ufn_ExcelToList()
{
	ds_list.ClearData();
	grd_list.SetCellProp("head", 0, "Text", "1");

	//trace(ds_excel.SaveXML());

	for(var i=1; i < ds_excel.GetRowCount() ; i++) {

		// 제조사 값이 있는 대상만 처리
		if(length(trim(ds_excel.GetColumn(i,6))) <> 0) {			
			if(length(ds_excel.GetColumn(i,7)) = 0 ) {

				gfn_showAlert("CW00022","( " + i + " ) 행의 발주번호" );
				ds_list.ClearData();
				return;
			}
			if(length(ds_excel.GetColumn(i,8)) = 0) {
				gfn_showAlert("CW00022","( " + i + " ) 행의 발주품목번호" );
				ds_list.ClearData();
				return;
			}

			ds_list.AddRow();
			ds_list.SetColumn(ds_list.row,"CHECK","1");
			ds_list.SetColumn(ds_list.row,"FLAG", "");
			ds_list.SetColumn(ds_list.row,"POSID", ds_excel.GetColumn(i,0));
			ds_list.SetColumn(ds_list.row,"POST1", ds_excel.GetColumn(i,1));
			ds_list.SetColumn(ds_list.row,"MATNR", ds_excel.GetColumn(i,2));
			ds_list.SetColumn(ds_list.row,"MATNR_NM", ds_excel.GetColumn(i,3));
			ds_list.SetColumn(ds_list.row,"BUDAT", ds_excel.GetColumn(i,4));						
			ds_list.SetColumn(ds_list.row,"LNAME", ds_excel.GetColumn(i,5));
			ds_list.SetColumn(ds_list.row,"JEJOSA", ds_excel.GetColumn(i,6));
			ds_list.SetColumn(ds_list.row,"EBELN", ds_excel.GetColumn(i,7));
			ds_list.SetColumn(ds_list.row,"EBELP", ds_excel.GetColumn(i,8));
			ds_list.SetColumn(ds_list.row,"LIFNR", "");
			ds_list.SetColumn(ds_list.row,"CRDAT", "");
			ds_list.SetColumn(ds_list.row,"SHIP_SCH", "");
		}	
	}
}
