function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	cal_date_to.value = gfn_getCurrDate();
	cal_date_fm.value = gfn_getBeforeDate(cal_date_to.value, 15);

	//권한 CHECK 부서코드 D4, D5 : 구매부 
	if (G_DPT1 == "D4" || G_DPT1 == "D5" || G_USER_ID == "2023091" || G_USER_ID == "7002542") {
		btn_create.Enable = true;
		btn_requote.Enable = false;
		btn_complete.Enable = true;
		ufn_enableBtn("delete", true);
		ufn_enableBtn("print",true);
	}
	else
	{	
		ed_lifnr.Value = G_VEND_NAME;
		ed_lifnr.Enable = false;
		ufn_enableBtn("delete", false);
		btn_create.Enable = false;
		btn_requote.Enable = true;
		btn_complete.Enable = false;
	}

    // 코드 DataSet 가져오기(구매담당자 코드 조회 ZLCODE CODE1 = "13")
    var codeArr = ["13"];
    var dsObjArr = [ds_ekgrp];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr,true);

	ufn_enableBtn("excel",  true);

	// 메세지
	gfn_showMsg("CI00007");	
}
