/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() {
    // Message 영역 초기화
	global.FRM_BOTTOM.img_msgType.imageId = "";
	global.FRM_BOTTOM.st_msg.value = "";

	// 필수 입력 항목 체크 
	if ( gfn_isNull(cal_date_fm, true) ) {
		gfn_showMsg("CW00001","견적작성일");
		return;
	}
	if ( gfn_isNull(cal_date_to, true) ) {
		gfn_showMsg("CW00001","견적작성일");
		return;
	}
	if ( !gfn_checkDate( cal_date_fm.value, cal_date_to.value, true, cal_date_to) ) {
		return;
	}

	//조회
	tit_clearActionInfo();
	tit_addSearchActionInfo("mm08:MM0801001A_S1");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true);


}

/********************************************************************************
* 기      능   : Print 
********************************************************************************/
function fn_print() {
	// 구매담당자별 출력물 호출
	gfn_copyDs(ds_list, ds_list_print,"CHECK=1");
	Dialog("MM08::MM0801001E.xml","", -1, -1, "Resize=true");

}
/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete(nRow) {

	// 저장여부 확인
	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	//  자료가 없으면 리턴
	if (ds_list2.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	ds_list.fireevent = false;

	for( var i=0; i<ds_list2.GetRowCount(); i++) 
	{
	// 견적작성 상태가 완료 일 경우 삭제 불가
		if ( ds_list2.GetColumn(i,"COMPL") == "2" )
		{
			gfn_showAlert("CW00072");
			ds_list.fireevent = true;
			return;
		}
	}
	ds_list.fireevent = true;

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm08:MM0801001A_U3");

	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterDelete");

}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {

   if ( !gfn_isChangeDs(ds_list, true) ) {
		return;
	}
	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}
	// 저장여부 확인
	if (!gfn_showConfirm("CI00010")) {
		return;
	}

	ds_list.fireevent = true;

	gfn_copyDs(ds_list, ds_list2, "CHECK=1");

	var cnt = ds_list2.GetRowCount();

	if (cnt > 0)
	{
		for( var i = 0; i < ds_list2.GetRowCount(); i++ ){

			if ( length(ds_list2.GetColumn(i,"MATNR")) < 1 ) 
			{
				gfn_showAlert("CW00001","자재번호");
				ds_list.fireevent = false;
				return;
			}
			if ( length(ds_list2.GetColumn(i,"LIFNR")) < 1 )
			{
				gfn_showAlert("CW00001","업체번호");
				ds_list.fireevent = false;
				return;
			}
			if ( length(ds_list2.GetColumn(i,"P_ZEINR") != 8) )
			{
				gfn_showAlert("CW00011","8");
				ds_list.fireevent = false;
				return;
			}

			if ( length(ds_list2.GetColumn(i,"RQDAT") != 8) )
			{
				gfn_showAlert("CW00011","8");
				ds_list.fireevent = false;
				return;
			}

			if ( ds_list2.GetColumn(i,"POSID") = '' || ds_list2.GetColumn(i,"POSID") = null )
			{
				ds_list2.SetColumn(i,"POSID","STOCK");
			}

		}
	}

	ds_list.fireevent = false;

	tit_clearActionInfo();
	tit_addSelectExActionInfo(
		"mm08:MM0801001A_S4"
	   ,"mm08:MM0801001A_I1"
	   ,"mm08:MM0801001A_U1");

	//저장 처리 
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2:A"
		, ""
		, ""
		, "fn_afterSave");

}
