/************************************************************************************/
// 기      능   :  완료버튼 클릭
/************************************************************************************/
function btn_complete_OnClick(obj)
{
	//  자료가 없으면 리턴
	if (ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");
		return;
	}

	// 저장여부 확인
	if (!gfn_showConfirm("CI00010")) {
		return;
	}	

	ds_list.fireevent = false;

	gfn_copyDs(ds_list, ds_list3, "CHECK=1");	

	var cnt = ds_list3.GetRowCount();

	if (cnt > 0)
	{
		for(var i=0; i < ds_list3.rowcount; i++)
		{
			if ( ds_list3.GetColumn(i,"COMPL") != "저장" || ds_list3.GetColumn(i,"CPDAT") == "")
			{
				gfn_showMsg("CW00017");
				ds_list.fireevent = true;
				return;	
			}

			if( ds_list3.GetColumn(i,"REDAT") == "" ) {

				gfn_showMsg("CW00017");
				ds_list.fireevent = true;
				return;
			}

			if (ds_list3.GetColumn(i,"CPDAT") < ds_list3.GetColumn(i,"REDAT")){

				gfn_showMsg("CW00017");
				ds_list.fireevent = true;
				return;
			}

			if ( length(ds_list3.GetColumn(i,"MATNR")) < 1 || length(ds_list3.GetColumn(i,"POSID")) < 1 ||
					 length(ds_list3.GetColumn(i,"LIFNR")) < 1  )
				{
					gfn_showMsg("CW00001");
					ds_list.fireevent = true;
					return;
				}
		}
	}

	ds_list.fireevent = true;

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm08:MM0801001A_U2");

	//저장 처리 
	tit_callService(
		""
		, ""
		, "ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave");
}
