		gfn_showAlert("CW00010");
		return;
	}

	ds_list.fireevent = false;

	tit_clearActionInfo();
	tit_addMultiActionInfo("mm08:MM0801001A_U4");

	tit_callService(
		""
		, ""
		, "ds_list4=ds_list4"
		, ""
		, ""
		, "fn_afterSave");

}
/************************************************************************************/
// 기      능   :  그리드 더블  클릭
/************************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//var arg = "ar_new=true";
	if ( ds_list.GetColumn(nRow,"ZKIND") == "2" || ds_list.GetColumn(nRow,"ZKIND") = "3")
	{
		var arg = "fa_qtnum=" + quote(ds_list.GetColumn(nRow,"QTNUM"));
			arg += " fa_matnr=" + quote(ds_list.GetColumn(nRow,"MATNR"));
			arg += " fa_lifnr=" + quote(ds_list.GetColumn(nRow,"LIFNR"));
			arg += " fa_netpr_if=" + quote(ds_list.GetColumn(nRow,"NETPR_IF"));
		var pop_obj = gfn_openPopup("MM0801001H", true, arg);
	} else {
		var arg = "fa_qtnum=" + quote(ds_list.GetColumn(nRow,"QTNUM"));
			arg += " fa_matnr=" + quote(ds_list.GetColumn(nRow,"MATNR"));
			arg += " fa_lifnr=" + quote(ds_list.GetColumn(nRow,"LIFNR"));
			arg += " fa_posid=" + quote(ds_list.GetColumn(nRow,"POSID"));
		var pop_obj = gfn_openPopup("MM0801001B", true, arg);

	}

	fn_query();
}
