/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
function fn_query() 
{		
		var qtnum = fa_qtnum;
		var posid = fa_posid;
		var matnr = fa_matnr;
		var lifnr = fa_lifnr;

	// 데이터셋 초기화
		ds_cond_1.ClearData();
		ds_cond_2.ClearData();
		ds_cond_3.ClearData();
		ds_cond_4.ClearData();
		ds_exCond.ClearData();
		ds_list_1.ClearData();
		ds_list_2.ClearData();
		ds_list_3.ClearData();
		ds_list_4.ClearData();
		ds_head.ClearData();
		ds_cnt.ClearData();
		ds_input.ClearData();

		//ds_head.ClearData();
		ed_cost1.Value = 0;
		ed_cost2.Value = 0;
		ed_total.Value = 0;
		ed_nego.Value = 0;

	//조회 조건 데이터셋 값 추가
		ds_cond_1.AddRow();
		ds_cond_1.SetColumn(0,"QTNUM",qtnum);
		ds_cond_1.SetColumn(0,"GUBN","1");
		ds_cond_2.AddRow();
		ds_cond_2.SetColumn(0,"QTNUM",qtnum);
		ds_cond_2.SetColumn(0,"GUBN","2");
		ds_cond_3.AddRow();
		ds_cond_3.SetColumn(0,"QTNUM",qtnum);
		ds_cond_3.SetColumn(0,"GUBN","3");
		ds_cond_4.AddRow();
		ds_cond_4.SetColumn(0,"QTNUM",qtnum);
		ds_cond_4.SetColumn(0,"GUBN","4");
		ds_exCond.AddRow();
		ds_exCond.SetColumn(0,"QTNUM",qtnum);
		ds_exCond.SetColumn(0,"POSID",posid);
		ds_exCond.SetColumn(0,"MATNR",matnr);
		ds_exCond.SetColumn(0,"LIFNR",lifnr);

	//조회	
		tit_clearActionInfo();
		tit_addSearchActionInfo("mm08:MM0801001B_S1");
		tit_addSearchActionInfo("mm08:MM0801001B_S2");
		tit_addSearchActionInfo("mm08:MM0801001B_S3");
		tit_addSearchActionInfo("mm08:MM0801001B_S4");
		tit_addSearchActionInfo("mm08:MM0801001B_S6");

		tit_callService(
			""
			, ""
			, "ds_cond_1=ds_cond_1 ds_cond_2=ds_cond_2 ds_cond_3=ds_cond_3 ds_cond_4=ds_cond_4 ds_exCond=ds_exCond"
			, "ds_list_1=ds_list_1 ds_list_2=ds_list_2 ds_list_3=ds_list_3 ds_list_4=ds_list_4 ds_cnt=ds_cnt"
			, ""
			, "fn_afterQuery"
			, true);

}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save()
{	
	var a = ToFloat(ds_list_1.Sum("TOT_NETPR")) + ToFloat(ds_list_2.Sum("TOT_NETPR")) +
		ToFloat(ds_list_3.Sum("TOT_NETPR")) + ToFloat(ds_list_4.Sum("TOT_NETPR"));

	ds_head.AddRow();
	ds_head.SetColumn(0,"QTNUM",fa_qtnum);
	ds_head.SetColumn(0,"MATNR",fa_matnr);
	ds_head.SetColumn(0,"LIFNR",fa_lifnr);
	ds_head.SetColumn(0,"POSID",fa_posid);
	ds_head.SetColumn(0,"TOT_COST1",ds_list_1.Sum("TOT_NETPR"));
	ds_head.SetColumn(0,"TOT_COST2",ds_list_2.Sum("TOT_NETPR"));
	ds_head.SetColumn(0,"TOT_COST3",ds_list_3.Sum("TOT_NETPR"));
	ds_head.SetColumn(0,"TOT_COST4",ds_list_4.Sum("NETPR"));
	ds_head.SetColumn(0,"TOT_COST5",ed_cost1.Value);
	ds_head.SetColumn(0,"TOT_COST6",ed_cost2.Value);
	ds_head.SetColumn(0,"TOT_COST7",ed_total.Value);
	ds_head.SetColumn(0,"TOT_COST8",ed_nego.Value);

	for(var i=0; i < ds_list_1.GetRowCount(); i++)
	{
		if( ds_list_1.GetColumn(i,"MENGE") == null){
			ds_list_1.SetColumn(i,"MENGE",0);
		}
		if( ds_list_1.GetColumn(i,"BDMNG") == null){
			ds_list_1.SetColumn(i,"BDMNG",0);
		}
		if( ds_list_1.GetColumn(i,"LOSS") == null){
			ds_list_1.SetColumn(i,"LOSS",0);
		}
		if( ds_list_1.GetColumn(i,"NETPR") == null) {
			ds_list_1.SetColumn(i,"NETPR",0);
		}
		if( ds_list_1.GetColumn(i,"TOT_MENGE") == null ) {
			ds_list_1.SetColumn(i,"TOT_MENGE",0);
		}
		if( ds_list_1.GetColumn(i,"TOT_NETPR") == null) {
			ds_list_1.SetColumn(i,"TOT_NETPR",0);
		}
	}

	for(var i=0; i < ds_list_2.GetRowCount(); i++)
	{
		if( ds_list_2.GetColumn(i,"MENGE") == null) {
			ds_list_2.SetColumn(i,"MENGE",0);
		}
		if( ds_list_2.GetColumn(i,"NETPR") == null) {
			ds_list_2.SetColumn(i,"NETPR",0);
		}
		if( ds_list_2.GetColumn(i,"TOT_NETPR") == null) {
			ds_list_2.SetColumn(i,"TOT_NETPR",0);
		}
	}

	for(var i=0; i< ds_list_3.GetRowCount(); i++)
	{
		if( ds_list_3.GetColumn(i,"MENGE") == null) {
			ds_list_3.SetColumn(i,"MENGE",0);
		}
		if( ds_list_3.GetColumn(i,"MENGE") == null) {
			ds_list_3.SetColumn(i,"MENGE",0);
		}
		if( ds_list_3.GetColumn(i,"MENGE") == null) {
			ds_list_3.SetColumn(i,"MENGE",0);
		}
	}

	for(var i=0; i < ds_list_4.GetRowCount(); i++)
	{
		if( ds_list_4.GetColumn(i,"NETPR") == null) {
			ds_list_4.SetColumn(i,"NETPR",0);
		}
	}

	tit_clearActionInfo();

	tit_addSaveActionInfo(
		"mm08:MM0801001B_I2"
	  , "mm08:MM0801001B_U2"
 	  , "mm08:MM0801001B_D1"
	  , "FLAG"
	  , ""
	  , 0
	  , "");

	tit_addSaveActionInfo(
		"mm08:MM0801001B_I3"
	  , "mm08:MM0801001B_U3"
 	  , "mm08:MM0801001B_D2"
	  , "FLAG"
	  , ""
	  , 0
	  , "");  

	tit_addSaveActionInfo(
		"mm08:MM0801001B_I4"
	  , "mm08:MM0801001B_U4"
 	  , "mm08:MM0801001B_D3"
	  , "FLAG"
	  , ""
	  , 0
	  , "");   

	tit_addSaveActionInfo(
		"mm08:MM0801001B_I5"
	  , "mm08:MM0801001B_U5"
 	  , "mm08:MM0801001B_D4"
	  , "FLAG"
	  , ""
	  , 0
	  , "");  

		if(ds_cnt.GetColumn(0,"CNT") > 0)
		{
			tit_addSingleActionInfo("mm08:MM0801001B_U1");
		} else {
			tit_addSingleActionInfo("mm08:MM0801001B_I1");	
		}

	 // 저장 처리 
		tit_callService(
			""
			, ""
			, "ds_list_1=ds_list_1 ds_list_2=ds_list_2 ds_list_3=ds_list_3 ds_list_4=ds_list_4 ds_head=ds_head"
			, ""
			, ""
			, "fn_afterSave"        
			); 
}

