// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(errCode, errMsg) 
{
	if ( ds_list_1.rowCount() > 0 || ds_list_2.rowCount() > 0 ||
	     ds_list_3.rowCount() > 0 || ds_list_4.rowCount() > 0) {
		gfn_showMsg("CI00002", "1 ");
	} else {
		//ds_head.SetColumn(0,"CHECK","1");
	}

	// 업체 입력한 부품 견적서 상태가 완료상태이면 전송취소도 못하도록 막음 
	if (ds_cnt.GetColumn(0,"PRO_F") == "4")
	{
		grd_list.Enable = false;
		grd_list2.Enable = false;
	    grd_list3.Enable = false;
	    grd_list4.Enable = false;
	    fn_plus1.Enable = false;
	    fn_plus2.Enable = false;
	    fn_plus3.Enable = false;
	    fn_plus4.Enable = false;
	    fn_minus1.Enable = false;
	    fn_minus2.Enable = false;
	    fn_minus3.Enable = false;
	    fn_minus4.Enable = false;
	    btn_save.Enable = false;
	    btn_send.Enable = false;
	    btn_cancel.Enable = false;
	    ed_nego.Enable = false;

	} 
	//부품견적서 상태가 전송 상태이면 전송취소만 가능하도록 막음
	else if (ds_cnt.GetColumn(0,"PRO_F") == "2")
	{
		grd_list.Enable = false;
		grd_list2.Enable = false;
	    grd_list3.Enable = false;
	    grd_list4.Enable = false;
	    fn_plus1.Enable = false;
	    fn_plus2.Enable = false;
	    fn_plus3.Enable = false;
	    fn_plus4.Enable = false;
	    fn_minus1.Enable = false;
	    fn_minus2.Enable = false;
	    fn_minus3.Enable = false;
	    fn_minus4.Enable = false;
	    btn_save.Enable = false;
	    btn_send.Enable = false;
	    btn_cancel.Enable = true;
	    ed_nego.Enable = false;
	} else {
		grd_list.Enable = true;
		grd_list2.Enable = true;
	    grd_list3.Enable = true;
	    grd_list4.Enable = true;
	    fn_plus1.Enable = true;
	    fn_plus2.Enable = true;
	    fn_plus3.Enable = true;
	    fn_plus4.Enable = true;
	    fn_minus1.Enable = true;
	    fn_minus2.Enable = true;
	    fn_minus3.Enable = true;
	    fn_minus4.Enable = true;
	    btn_save.Enable = true;
	    btn_send.Enable = true;
	    btn_cancel.Enable = false;
	    ed_nego.Enable = true;
	}
	ed_nego.Value = ds_cnt.GetColumn(0,"TOT_COST8");
	fn_cost();

}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg)
{
	fn_query();
}

